Action.Str:
/* --- Java Code to Format Override Data for Chart.js --- */

/* Create a buffer to hold the formatted data string */
scatter_data_str = new StringBuffer();

/* 
 * FIX: Do not rely on %AAB_Overrides(#) injection.
 * In exported apps, this injection can fail or cause syntax errors.
 * Instead, we manually loop through indices 1, 2, 3... until we find null.
*/
i = 1;

while (true) {
    /* Construct the variable name for the current index (e.g., AAB_Overrides1) */
    varName = "AAB_Overrides" + i;

    /* Retrieve the value using the tasker object */
    overrideData = tasker.getVariable(varName);

    /* If the variable returns null, we have reached the end of the array */
    if (overrideData == null) {
        break;
    }

    /* Process the valid data point */
    try {
        /* Read the "lux,brightness" string */
        parts = overrideData.split(",");

        if (parts.length == 2) {
            /* Add a comma if this is not the first data point added to the buffer */
            if (scatter_data_str.length() > 0) {
                scatter_data_str.append(",");
            }

            /* Append the data in the format {x:..., y:...} */
            /* Using .trim() ensures no accidental whitespace breaks the JSON */
            scatter_data_str.append("{x:" + parts[0].trim() + ",y:" + parts[1].trim() + "}");
        }
    } catch (Exception e) { 
        /* Ignore malformed data lines to prevent crashing */
    }

    /* Increment index for the next loop */
    i++;

    /* Safety brake: prevent infinite loop in case of corrupt memory (optional but safe) */
    if (i > 500) break;
}

/* Set the final variable that the HTML template will use */
tasker.setVariable("scatter_data", scatter_data_str.toString());

/* --- Java Code for Graph Data Generation (V15.8 Compatible) --- */

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* --- Read all necessary Tasker variables --- */
double aab_zone1end = %aab_zone1end;
double aab_zone2end = %aab_zone2end;
double aab_form1a = %aab_form1a;
double aab_form2a = %aab_form2a;
double aab_form2b = %aab_form2b;
double aab_form2c = %aab_form2c;
double aab_form3a = %aab_form3a;
double AAB_MinBright = %AAB_MinBright;
double AAB_MaxBright = %AAB_MaxBright;

/* --- The hardcoded lux values from the For loop --- */
double[] luxValues = {
    0.1,0.35,1,1.47,2.15,3.16,4.64,6.81,10,13.34,17.78,23.71,31.62,42.17,56.24,75.01,100,133.4,177.8,237.1,316.2,421.7,562.4,750.1,1000,1334,1778,2371,3162,4217,5624,7501,10000,13340,17783,23714,31623,42174,56234,75011,100000
};

/* --- Initialize the lists to hold our data points --- */
List lux_labels_list = new ArrayList();
List new_data_list = new ArrayList();
List ref_data_list = new ArrayList();

/* --- The main calculation loop --- */
for (int i = 0; i < luxValues.length; i++) {
    double lux = luxValues[i];
    double new_point = 0.0;
    double ref_point = 0.0;

    /* --- Calculate New Brightness Point --- */
    if (lux < aab_zone1end) {
        new_point = aab_form1a * Math.sqrt(lux);
    } else if (lux < aab_zone2end) {
        new_point = aab_form2a + aab_form2b * (Math.pow(lux - aab_form2c, 0.33) - Math.pow(aab_zone1end - aab_form2c, 0.33));
    } else {
        /* --- THE FIX IS HERE: Simplified formula with form3b = 1.0 --- */
        new_point = AAB_MaxBright - (aab_form3a / lux) * AAB_MaxBright;
    }

    /* --- Clamp the new_point to the min/max boundaries --- */
    if (new_point < AAB_MinBright) { new_point = AAB_MinBright; }
    if (new_point > AAB_MaxBright) { new_point = AAB_MaxBright; }

    /* --- Calculate Reference Brightness Point --- */
    if (lux < 50) {
        ref_point = 5.0 * Math.sqrt(lux);
    } else if (lux < 10000) {
        ref_point = 29.58 + 8.8 * (Math.pow(lux - 18.0, 0.33) - Math.pow(35.0 - 18.0, 0.33));
    } else {
        ref_point = 255.0 - ((2513.0 / lux) * 255.0);
    }

    /* --- Clamp the ref_point to the min/max boundaries --- */
    if (ref_point < AAB_MinBright) { ref_point = AAB_MinBright; }
    if (ref_point > AAB_MaxBright) { ref_point = AAB_MaxBright; }

    /* --- Add the calculated points to our lists --- */
    lux_labels_list.add(new Double(lux));
    new_data_list.add(new Double(new_point));
    ref_data_list.add(new Double(ref_point));
}

/* --- Join the lists into comma-separated strings --- */
StringBuffer lux_labels_str = new StringBuffer();
StringBuffer new_data_str = new StringBuffer();
StringBuffer ref_data_str = new StringBuffer();

for (int i = 0; i < lux_labels_list.size(); i++) {
    if (i > 0) {
        lux_labels_str.append(",");
        new_data_str.append(",");
        ref_data_str.append(",");
    }
    lux_labels_str.append(lux_labels_list.get(i));
    new_data_str.append(new BigDecimal(((Double) new_data_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
    ref_data_str.append(new BigDecimal(((Double) ref_data_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
}

/* --- Set the final variables for Tasker to use --- */
tasker.setVariable("lux_labels", lux_labels_str.toString());
tasker.setVariable("new_data", new_data_str.toString());
tasker.setVariable("ref_data", ref_data_str.toString());

import android.provider.Settings;
import android.content.ContentResolver;

/* --- Safe Initialization Phase --- */

/* 1. Get Content Resolver */
cResolver = context.getContentResolver();

/* 2. Get Current Brightness (Safely) */
try {
    startBrightness = Settings.System.getInt(cResolver, Settings.System.SCREEN_BRIGHTNESS);
} catch (Exception e) {
    tasker.log("AAB: Could not read system brightness. Aborting.");
    return; 
}

/* 3. Get Target Brightness (%par1) */
targetStr = tasker.getVariable("par1");
if (targetStr == null) {
    tasker.log("AAB: No target brightness (%par1) set. Aborting.");
    return;
}
targetBrightness = Double.parseDouble(targetStr);

/* 4. Get Configuration (%par2 -> loops,wait) */
par2 = tasker.getVariable("par2");
if (par2 == null) par2 = "20,30"; /* Default safety fallback */
parts = par2.split(",");
loops = Integer.parseInt(parts[0]);
wait = Long.parseLong(parts[1]);

/* 5. Get Global Settings */
dimmingThresholdStr = tasker.getVariable("AAB_DimmingThreshold");
dimmingThreshold = (dimmingThresholdStr != null) ? Double.parseDouble(dimmingThresholdStr) : 5.0;

dimmingEnabledStr = tasker.getVariable("AAB_DimmingEnabled");
dimmingEnabled = (dimmingEnabledStr != null && dimmingEnabledStr.equals("true"));

detectOverridesStr = tasker.getVariable("AAB_DetectOverrides");
detectOverrides = (detectOverridesStr != null && detectOverridesStr.equals("On"));

/* 6. Calculate Min/Max Targets */
if (startBrightness < targetBrightness) {
    minTarget = startBrightness;
    maxTarget = targetBrightness + 1;
} else {
    minTarget = targetBrightness - 1;
    maxTarget = startBrightness;
}

/* Export these for the Debug Flash (A13) */
tasker.setVariable("max_target", String.valueOf(maxTarget));
tasker.setVariable("min_target", String.valueOf(minTarget));
tasker.setVariable("loops", String.valueOf(loops));
tasker.setVariable("wait", String.valueOf(wait));

/* --- Execution Phase --- */

engineStartTime = System.currentTimeMillis();
consecutiveOutOfBounds = 0;
overrideTriggerThreshold = 2; 
previousBrightnessInt = -1;

tasker.log("--- Starting AAB Animation: " + startBrightness + " -> " + targetBrightness + " ---");

for (int counter = 0; counter < loops; counter++) {

    /* Check for manual stop flag - Doing this every frame is necessary for responsiveness, 
       but we avoid the IPC overhead of writing variables unless necessary below. */
    override = tasker.getVariable("AAB_Manual_Override");
    if ("true".equals(override)) {
        tasker.log("Manual override detected via variable. Stopping.");
        break;
    }

    /* Calculate interpolation */
    progress = (double)counter / loops;
    calculated_brightness_smooth = startBrightness + progress * (targetBrightness - startBrightness);

    /* Apply Brightness to System (Every Frame for Smoothness) */
    brightnessInt = (int) Math.round(calculated_brightness_smooth);
    if (brightnessInt < 0) brightnessInt = 0;
    if (brightnessInt > 255) brightnessInt = 255;

    /* Write to system settings ONLY if value changed */
    if (brightnessInt != previousBrightnessInt) {
        Settings.System.putInt(cResolver, Settings.System.SCREEN_BRIGHTNESS, brightnessInt);
        previousBrightnessInt = brightnessInt;
    }

    /* 
     * OPTIMIZATION: Only update Tasker variables and check overrides every 5th frame.
     */
    if (counter % 5 == 0 || counter == loops - 1) {

        /* 1. Format Display Variable (math-based for efficiency instead of BigDecimal) */
        AAB_CurrentBright_str = "";
        if (calculated_brightness_smooth < dimmingThreshold) {
            /* Round to 1 decimal place: (int)(val * 10) / 10.0 */
            roundedVal = Math.round(calculated_brightness_smooth * 10.0) / 10.0;
            AAB_CurrentBright_str = String.valueOf(roundedVal);
        } else {
            AAB_CurrentBright_str = String.valueOf(Math.round(calculated_brightness_smooth));
        }

        /* 2. Update Global Variable */
        tasker.setVariable("AAB_CurrentBright", AAB_CurrentBright_str);

        /* 3. Dimming Decider Hook */
        if (dimmingEnabled) {
            tasker.callTask("Dimming Decider", null);
        }

        /* 4. Override Detection */
        if (detectOverrides) {
            try {
                actualSystemBrightness = Settings.System.getInt(cResolver, Settings.System.SCREEN_BRIGHTNESS);

                if (actualSystemBrightness > maxTarget + 2 || actualSystemBrightness < minTarget - 2) {
                    consecutiveOutOfBounds++;

                    if (consecutiveOutOfBounds >= overrideTriggerThreshold) {
                        tasker.log("Hardware override detected (System: " + actualSystemBrightness + " vs Calc: " + brightnessInt + "). Stopping.");
                        tasker.setVariable("AAB_Manual_Override", "true");
                        tasker.callTask("Manual Override", null);
                        break;
                    }
                } else {
                    consecutiveOutOfBounds = 0;
                }
            } catch (Exception e) {
                /* Ignore read errors during loop */
            }
        }
    }

    /* Self-Adjusting Wait */
    timeSpentSoFar = System.currentTimeMillis() - engineStartTime;
    targetTimeForStep = (long)((counter + 1) * wait);
    adjustedWait = targetTimeForStep - timeSpentSoFar;

    try {
        if (adjustedWait > 0 && counter < loops - 1) {
            Thread.sleep(adjustedWait);
        }
    } catch (InterruptedException e) {
        break;
    }
}

engineEndTime = System.currentTimeMillis();
tasker.setVariable("java_loop_duration", String.valueOf(engineEndTime - engineStartTime));
tasker.setVariable("AutoBrightRunning", "0");

import java.util.HashMap;
import android.provider.Settings;
import android.content.ContentResolver;

/* --- Start Timer --- */
engineStartTime = System.currentTimeMillis();

/* --- Read Parameters --- */
targetBrightness = Double.parseDouble(tasker.getVariable("calculated_brightness"));
finalDim = Double.parseDouble(tasker.getVariable("final_dim"));
loops = Integer.parseInt(tasker.getVariable("loops"));
wait = Integer.parseInt(tasker.getVariable("wait"));

/* Continuity Fix */
startBrightness = Double.parseDouble(tasker.getVariable("currentbrightness"));

/* Animation Config */
dimmingThreshold = Double.parseDouble(tasker.getVariable("AAB_DimmingThreshold"));
dimStart = Double.parseDouble(tasker.getVariable("dim_start"));

/* Feature Flags */
privilege = tasker.getVariable("AAB_Privilege");
detectOverrides = "On".equals(tasker.getVariable("AAB_DetectOverrides"));

/* Logic Flags */
isUnprivileged = "None".equals(privilege);
isNativeSecure = "Write Secure".equals(privilege);
requiresTaskCall = !isUnprivileged && !isNativeSecure;

/* System Access */
cResolver = context.getContentResolver();
consecutiveOutOfBounds = 0;

/* --- LOGIC UPGRADE: Time-Based Override Threshold --- */
/* Reduced threshold to 2 because we are checking less frequently (every 5 frames) */
overrideTriggerThreshold = 2;

/* --- STATE TRACKING (Optimization) --- */
/* Track previous values to avoid redundant system writes (Battery Saver & Speed) */
previousHardwareTarget = -1;
previousDimVal = -1;
previousAlpha = -1;

/* Optimization: Cache Dimming Status locally to avoid IPC read inside loop */
dimmingStatusVar = tasker.getVariable("AAB_DimmingStatus");
dimmingStatusActive = (dimmingStatusVar != null && dimmingStatusVar.equals("1"));

dimParams = new HashMap();
overlayParams = new HashMap();

/* Initialize tracking variable outside loop */
calculated_bright = startBrightness;

for (counter = 0; counter < loops; counter++) {

    /* 1. Safety Check */
    if ("true".equals(tasker.getVariable("AAB_Manual_Override"))) break;

    /* 2. Calculate Values */
    progress = (double)counter / loops;
    calculated_bright = startBrightness + progress * (targetBrightness - startBrightness);

    /* Dimming Curve */
    dim_shell_double = dimStart + progress * (finalDim - dimStart);
    dim_val = (int) Math.round(dim_shell_double);

    /* 3. Determine Hardware Target (PWM Logic) */
    hardwareTarget = 0;
    if (calculated_bright < dimmingThreshold) {
        hardwareTarget = (int) Math.round(dimmingThreshold);
    } else {
        hardwareTarget = (int) Math.round(calculated_bright);
    }

    /* OPTIMIZATION: Write Hardware Brightness ONLY if changed */
    if (hardwareTarget != previousHardwareTarget) {
        Settings.System.putInt(cResolver, Settings.System.SCREEN_BRIGHTNESS, hardwareTarget);
        previousHardwareTarget = hardwareTarget;
    }

    /* 4. Override Detection (Throttled) */
    if (detectOverrides) {
        /* OPTIMIZATION: Check only every 5 frames to reduce CPU load */
        if (counter % 5 == 0) {
            try {
                actualBright = Settings.System.getInt(cResolver, Settings.System.SCREEN_BRIGHTNESS);

                /* Tolerance is 10% of target, but minimum 4 units */
                dynamicTolerance = (int) Math.max(4, hardwareTarget * 0.10);

                if (Math.abs(actualBright - hardwareTarget) > dynamicTolerance) {
                    consecutiveOutOfBounds++;
                    if (consecutiveOutOfBounds >= overrideTriggerThreshold) {
                        tasker.setVariable("AAB_Manual_Override", "true");
                        tasker.callTask("Manual Override", new HashMap()); 
                        break;
                    }
                } else {
                    consecutiveOutOfBounds = 0;
                }
            } catch (Exception e) {}
        }
    }

    /* 5. APPLY DIMMING LOGIC */

    if (isUnprivileged) {
        /* Check local cached boolean instead of IPC call */
        if (!dimmingStatusActive) {
             tasker.callTask("_ShowColorFilter", new HashMap());
             tasker.setVariable("AAB_DimmingStatus", "1");
             dimmingStatusActive = true;
        }

        alphaVal = dim_val;
        if (alphaVal > 253) alphaVal = 253;
        if (alphaVal < 0) alphaVal = 0;

        /* OPTIMIZATION: Only calculate hex and update overlay if Alpha Value Changed */
        if (alphaVal != previousAlpha) {
            hexStr = Integer.toHexString(alphaVal);
            if (hexStr.length() < 2) hexStr = "0" + hexStr;
            colorCode = "#" + hexStr + "000000";

            tasker.setVariable("AAB_HexOverlay", colorCode);
            overlayParams.put("par1", colorCode);
            tasker.callTask("_OverlayColorUpdate", overlayParams);

            previousAlpha = alphaVal;
        }

        /* OPTIMIZATION: Throttle global status updates */
        if (counter % 5 == 0) {
            dimPct = dim_shell_double / 2.55;
            /* Primitive math rounding replaces BigDecimal */
            roundedDim = Math.round(dimPct * 10.0) / 10.0;
            tasker.setVariable("AAB_DimmingCurrent", String.valueOf(roundedDim));
            tasker.setVariable("AAB_DimmingDS", String.valueOf(dim_val));
        }
    } 
    else if (isNativeSecure) {
        try {
            if (dim_val > 100) dim_val = 100;
            if (dim_val < 0) dim_val = 0;

            /* OPTIMIZATION: Only write Secure Setting if value changed */
            if (dim_val != previousDimVal) {
                Settings.Secure.putInt(cResolver, "reduce_bright_colors_level", dim_val);
                previousDimVal = dim_val;
            }

            if (!dimmingStatusActive) {
                Settings.Secure.putInt(cResolver, "reduce_bright_colors_activated", 1);
                tasker.setVariable("AAB_DimmingStatus", "1");
                dimmingStatusActive = true;
            }

            /* OPTIMIZATION: Throttle global status updates */
            if (counter % 5 == 0) {
                roundedDim = Math.round(dim_shell_double * 10.0) / 10.0;
                tasker.setVariable("AAB_DimmingCurrent", String.valueOf(roundedDim));
                tasker.setVariable("AAB_DimmingDS", String.valueOf(dim_val));
            }
        } catch (Exception e) {}
    }
    else if (requiresTaskCall) {
        /* Task Call Logic with fixed Guard clauses to prevent Rejected Copy errors. */
        isLastLoop = (counter == loops - 1);

        /* Get fresh TRUN value */
        trun = tasker.getVariable("TRUN");
        taskName = "Apply Dimming (Privileged)";

        /* FRAME DROPPING LOGIC */
        /* If the task is running... */
        if (trun != null && trun.contains(taskName)) {
            /* If it's the last loop, we MUST wait for the previous task to finish
               so we can apply the final brightness value reliably. */
            if (isLastLoop) {
                /* wait for the previous instance to finish (No timeout!) */
                while (trun.contains(taskName)) {
                    try { Thread.sleep(5); } catch (Exception e) {}
                    trun = tasker.getVariable("TRUN");
                }
            } 
            /* If it's NOT the last loop, just skip this frame to prevent 
               stacking/rejected copy errors. Maintains animation speed. */
            else {
                continue; 
            }
        }

        /* PREPARE AND CALL */
        dimParams.put("dim_shell", String.valueOf(dim_shell_double));
        tasker.callTask(taskName, dimParams);

        /* CRITICAL FIX: START-UP GUARD (Only needed for Last Loop reliability) */
        /* If this is the last loop, we want to make sure the task we just called 
           actually starts before we finish the script, ensuring the UI is consistent. */
        if (isLastLoop) {
             sTime = System.currentTimeMillis();
             /* Wait up to 200ms for the task to appear in TRUN */
             trun = tasker.getVariable("TRUN");
             while ((trun == null || !trun.contains(taskName)) && (System.currentTimeMillis() - sTime < 200)) {
                 try { Thread.sleep(5); } catch (Exception e) {}
                 trun = tasker.getVariable("TRUN");
             }
        }
    }

    /* 6. Update UI Variables */
    /* OPTIMIZATION: Throttle UI Global updates to every 5 frames */
    /* OPTIMIZATION: Use Primitive Math instead of BigDecimal object allocation */
    if (counter % 5 == 0) {
        currBrightStr = "";
        if (calculated_bright < dimmingThreshold) {
             /* Round to 1 decimal place: (int)(val * 10) / 10.0 */
             roundedVal = Math.round(calculated_bright * 10.0) / 10.0;
             currBrightStr = String.valueOf(roundedVal);
        } else {
             currBrightStr = String.valueOf(Math.round(calculated_bright));
        }
        tasker.setVariable("AAB_CurrentBright", currBrightStr);
    }

    /* 7. Wait Loop */
    timeSpent = System.currentTimeMillis() - engineStartTime;
    targetTime = (long)((counter + 1) * wait);
    adjustedWait = targetTime - timeSpent;

    try {
        if (adjustedWait > 0 && counter < loops - 1) {
            Thread.sleep(adjustedWait);
        }
    } catch (InterruptedException e) { break; }
}

/* Post-Loop Cleanup */
if (!"true".equals(tasker.getVariable("AAB_Manual_Override"))) {
    calculated_bright = targetBrightness;
}

/* Final update to ensure target is reached exactly in UI (Primitive Math) */
if (calculated_bright < dimmingThreshold) {
     roundedVal = Math.round(calculated_bright * 10.0) / 10.0;
     currBrightStr = String.valueOf(roundedVal);
} else {
     currBrightStr = String.valueOf(Math.round(calculated_bright));
}
tasker.setVariable("AAB_CurrentBright", currBrightStr);

endTime = System.currentTimeMillis();
tasker.setVariable("java_loop_duration", String.valueOf(endTime - engineStartTime));
tasker.setVariable("AutoBrightRunning", "0");
tasker.setVariable("calculated_brightness_smooth", String.valueOf(calculated_bright));

/* --- Java Code for Reactivity Graph Data Generation --- */

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* --- Read all necessary Tasker variables safely --- */
/* Helper method to parse doubles safely from Tasker variables */
double getDouble(String name) {
    try {
        String val = tasker.getVariable(name);
        return (val == null || val.length() == 0) ? 0.0 : Double.parseDouble(val);
    } catch (Exception e) {
        return 0.0;
    }
}

double zone1end = getDouble("aab_zone1end");
double threshdark = getDouble("aab_threshdark");
double threshdim = getDouble("aab_threshdim");
double threshbright = getDouble("aab_threshbright");
double threshsteepness = getDouble("aab_threshsteepness");
double threshmidpoint = getDouble("aab_threshmidpoint");

/* --- The hardcoded lux values from the Tasker For loop --- */
double[] luxValues = {
    1, 1.47, 2.15, 3.16, 4.64, 6.81, 
    10, 13.34, 17.78, 23.71, 31.62, 42.17, 56.24, 75.01, 
    100, 133.4, 177.8, 237.1, 316.2, 421.7, 562.4, 750.1, 
    1000, 1334, 1778, 2371, 3162, 4217, 5624, 7501, 
    10000, 13340, 17783, 23714, 31623, 42174, 56234, 75011, 100000
};

/* --- Initialize the lists to hold our data points --- */
List lux_labels_list = new ArrayList();
List new_data_list = new ArrayList();
List ref_data_list = new ArrayList();

/* --- The main calculation loop --- */
for (int i = 0; i < luxValues.length; i++) {
    double lux = luxValues[i];
    double new_point = 0.0;
    double ref_point = 0.0;

    /* --- Calculate New Reactivity Threshold Point --- */
    if (lux < zone1end) {
        /* Linear interpolation for dark zone */
        /* Tasker: (%aab_threshdark - ((%aab_threshdark - %aab_threshdim) / %aab_zone1end) * %lux) * 100 */
        new_point = (threshdark - ((threshdark - threshdim) / zone1end) * lux) * 100.0;
    } else {
        /* Sigmoid curve for brighter zones */
        /* Tasker: (%aab_threshdim + (%aab_threshbright - %aab_threshdim) / (1 + e^(-%aab_threshsteepness * (log10(%lux+1) - %aab_threshmidpoint)))) * 100 */
        double exponent = -threshsteepness * (Math.log10(lux + 1) - threshmidpoint);
        double denominator = 1.0 + Math.exp(exponent);
        new_point = (threshdim + (threshbright - threshdim) / denominator) * 100.0;
    }

    /* --- Calculate Reference Threshold Point --- */
    if (lux < 35.0) {
        /* Linear interpolation reference */
        /* Tasker: (0.30 - ((0.30 - 0.25) / 35) * %lux) * 100 */
        ref_point = (0.30 - ((0.30 - 0.25) / 35.0) * lux) * 100.0;
    } else {
        /* Sigmoid curve reference */
        /* Tasker: (0.25 + (0.08 - 0.25) / (1 + e^(-2.1 * (log10(%lux+1) - 4)))) * 100 */
        double exponentRef = -2.1 * (Math.log10(lux + 1) - 4.0);
        double denominatorRef = 1.0 + Math.exp(exponentRef);
        ref_point = (0.25 + (0.08 - 0.25) / denominatorRef) * 100.0;
    }

    /* --- Add the calculated points to our lists --- */
    lux_labels_list.add(new Double(lux));
    new_data_list.add(new Double(new_point));
    ref_data_list.add(new Double(ref_point));
}

/* --- Join the lists into comma-separated strings --- */
StringBuffer lux_labels_str = new StringBuffer();
StringBuffer new_data_str = new StringBuffer();
StringBuffer ref_data_str = new StringBuffer();

for (int i = 0; i < lux_labels_list.size(); i++) {
    if (i > 0) {
        lux_labels_str.append(",");
        new_data_str.append(",");
        ref_data_str.append(",");
    }

    lux_labels_str.append(lux_labels_list.get(i));

    /* Format to 3 decimal places using BigDecimal */
    new_data_str.append(new BigDecimal(((Double) new_data_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
    ref_data_str.append(new BigDecimal(((Double) ref_data_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
}

/* --- Set the final variables for Tasker to use --- */
tasker.setVariable("lux_labels", lux_labels_str.toString());
tasker.setVariable("new_data", new_data_str.toString());
tasker.setVariable("ref_data", ref_data_str.toString());

/* --- Java Code for Circadian Dimming Graph Generation --- */

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* --- Helper to safely parse doubles from Tasker --- */
double getDouble(String name) {
    try {
        String val = tasker.getVariable(name);
        return (val == null || val.length() == 0) ? 0.0 : Double.parseDouble(val);
    } catch (Exception e) {
        return 0.0;
    }
}

/* --- Read Input Variables --- */
double dimspread = getDouble("aab_dimspread");
double transition = getDouble("AAB_ScaleTransitionFactor");
double steepness = getDouble("AAB_ScaleSteepness");
double sunlightDuration = getDouble("AAB_Sunlightduration");
String polarState = tasker.getVariable("AAB_PolarState");

/* --- Sun Event Calculations (A7) --- */
/* Normalize to seconds of the day */
double raw_dawn = getDouble("AAB_Sundawn");
double raw_sunrise = getDouble("AAB_Sunrise");
double raw_noon = getDouble("AAB_Sunnoon");
double raw_sunset = getDouble("AAB_Sunset");
double raw_dusk = getDouble("AAB_Sundusk");

double calc_dawn = raw_dawn % 86400;
double calc_sunrise = raw_sunrise % 86400;
double calc_noon = raw_noon % 86400;
double calc_sunset = raw_sunset % 86400;
double calc_dusk = raw_dusk % 86400;

/* --- Day Wrap Logic (A8 - A22) --- */
if (calc_dawn > calc_sunrise) { calc_dawn -= 86400; }
if (calc_noon < calc_sunrise) { calc_noon += 86400; }
if (calc_sunset < calc_sunrise) { calc_sunset += 86400; }
if (calc_dusk < calc_sunset) { calc_dusk += 86400; }

if (calc_dawn < 0) {
    calc_dawn += 86400;
    calc_sunrise += 86400;
    calc_noon += 86400;
    calc_sunset += 86400;
    calc_dusk += 86400;
}

/* --- Window Duration Calculations (A23) --- */
double daylength = calc_sunset - calc_sunrise;
double delta_day = daylength * transition;
double nightlength = 86400 - daylength;
double delta_night = nightlength * transition;

double morning_start = calc_dawn - delta_night;
double morning_end = calc_sunrise + delta_day;
double evening_start = calc_sunset - delta_day;
double evening_end = calc_dusk + delta_night;

double morning_duration = morning_end - morning_start;
double evening_duration = evening_end - evening_start;

/* --- Main Loop for Data Points (A27 - A81) --- */
/* Loop from 0 to 86400 in 600s increments */
StringBuffer time_labels_str = new StringBuffer();
StringBuffer data_points_str = new StringBuffer();

for (int sim_now = 0; sim_now <= 86400; sim_now += 600) {

    double progress = 0.0;
    boolean progress_found = false;
    double time_v1 = (double) sim_now;
    double time_v2 = time_v1 + 86400;

    /* --- Logic: Determine "Progress" (0=Night, 1=Day) --- */
    if ("true".equals(polarState)) {
        // Polar Day (sunlight > 1380 mins) or Polar Night (< 60 mins)
        if (sunlightDuration > 1380) { progress = 1.0; } 
        else if (sunlightDuration < 60) { progress = 0.0; }
        else { progress = 0.0; } // Default fallback
    } else {
        // Standard Day/Night Logic

        // Morning Transition (0 -> 1)
        if (time_v1 > morning_start && time_v1 < morning_end) {
            progress = (time_v1 - morning_start) / morning_duration;
            progress_found = true;
        } else if (time_v2 > morning_start && time_v2 < morning_end) {
            progress = (time_v2 - morning_start) / morning_duration;
            progress_found = true;
        }
        // Evening Transition (1 -> 0)
        else if (time_v1 > evening_start && time_v1 < evening_end) {
            progress = 1.0 - ((time_v1 - evening_start) / evening_duration);
            progress_found = true;
        } else if (time_v2 > evening_start && time_v2 < evening_end) {
            progress = 1.0 - ((time_v2 - evening_start) / evening_duration);
            progress_found = true;
        }
        // Full Day or Full Night
        else {
            boolean is_day = false;
            if ((time_v1 > morning_end && time_v1 < evening_start) || 
                (time_v2 > morning_end && time_v2 < evening_start)) {
                is_day = true;
            }
            progress = is_day ? 1.0 : 0.0;
        }
    }

    /* Clamp Progress */
    if (progress > 0.99) progress = 1.0;
    if (progress < 0.01) progress = 0.0;

    /* --- Logic: Curve Smoothing (Tanh) --- */
    double x_factor = (progress - 0.5) * steepness;
    double neg_x_factor = -1.0 * x_factor;

    // Tanh calculation: (e^x - e^-x) / (e^x + e^-x)
    double exp_steep = Math.exp(steepness / 2.0);
    double exp_neg_steep = Math.exp(-steepness / 2.0);
    double tanh_max = (exp_steep - exp_neg_steep) / (exp_steep + exp_neg_steep);

    double exp_x = Math.exp(x_factor);
    double exp_neg_x = Math.exp(neg_x_factor);
    double tanh_raw = (exp_x - exp_neg_x) / (exp_x + exp_neg_x);

    double modifier = 0.0;
    if (tanh_max != 0) {
        modifier = tanh_raw / tanh_max;
    }

    /* Final Dim Modifier Value */
    double dim_val = 2.0 - (1.0 + (dimspread / 100.0) * modifier);

    /* --- Formatting --- */
    if (sim_now > 0) {
        time_labels_str.append(",");
        data_points_str.append(",");
    }

    // Round data point to 3 decimals
    data_points_str.append(new BigDecimal(dim_val).setScale(3, BigDecimal.ROUND_HALF_UP).toString());

    /* --- FIX: Explicitly calculate hour and minute variables --- */
    int hour = sim_now / 3600;
    int minute = (sim_now % 3600) / 60;

    /* Format HH:MM using calculated variables */
    String hStr = (hour < 10) ? "0" + hour : "" + hour;
    String mStr = (minute < 10) ? "0" + minute : "" + minute;
    time_labels_str.append("'" + hStr + ":" + mStr + "'");
}

/* --- Calculate NOW_UTC (A82) --- */
long nowTime = System.currentTimeMillis() / 1000;
long nowUtc = nowTime % 86400;

/* --- Set Tasker Variables --- */
// Graph Data
tasker.setVariable("time_labels", time_labels_str.toString());
tasker.setVariable("data_points", data_points_str.toString());
tasker.setVariable("now_utc", String.valueOf(nowUtc));

// Calculated Sun Events (Needed for HTML replacement later)
tasker.setVariable("sim_calc_dawn", String.valueOf((long)calc_dawn));
tasker.setVariable("sim_calc_noon", String.valueOf((long)calc_noon));
tasker.setVariable("sim_calc_sunset", String.valueOf((long)calc_sunset));
tasker.setVariable("sim_calc_dusk", String.valueOf((long)calc_dusk));

/* --- Java Code for Lux Smoothing (Enhanced with Rounding) --- */

import java.math.BigDecimal;

/* Read literal values inserted by Tasker */
double par1 = %par1;
double par2 = %par2;
double AAB_ThreshDynamic = %AAB_ThreshDynamic;
double AAB_DeltaFactor = %AAB_DeltaFactor;
double AAB_Zone1End = %AAB_Zone1End;

/* A1: Calculate lux_delta */
double lux_delta_raw = Math.abs((par1 - par2) / (par2 + 1.0));
double lux_delta = Math.round(lux_delta_raw * 1000.0) / 1000.0;

/* A2: Calculate effective_delta */
double effective_delta_raw = lux_delta - (AAB_ThreshDynamic / 100.0);
double effective_delta = Math.round(effective_delta_raw * 1000.0) / 1000.0;

/* A3: Calculate lux_alpha */
double lux_alpha_raw = 1.0 - Math.exp(-AAB_DeltaFactor * effective_delta);
double lux_alpha = Math.round(lux_alpha_raw * 1000.0) / 1000.0;

/* A4: Calculate new_smoothed_lux (unrounded) */
double new_smoothed_lux_raw = (par1 * lux_alpha) + (par2 * (1.0 - lux_alpha));

/* --- NEW LOGIC: Using BigDecimal for readable rounding --- */

/* Create a BigDecimal object from the calculated double */
BigDecimal luxToRound = new BigDecimal(new_smoothed_lux_raw);

String final_lux_string;
if (new_smoothed_lux_raw < AAB_Zone1End) {
    /* Round to 2 decimal places using HALF_UP rounding (standard) */
    BigDecimal roundedLux = luxToRound.setScale(2, BigDecimal.ROUND_HALF_UP);
    final_lux_string = roundedLux.toString();
} else {
    /* Round to 0 decimal places. This will not have a ".0" */
    BigDecimal roundedLux = luxToRound.setScale(0, BigDecimal.ROUND_HALF_UP);
    final_lux_string = roundedLux.toString();
}

/* Return the final values, using the correctly formatted string */
return final_lux_string + "," + lux_alpha + "," + lux_delta;

/* --- Java Code for Animation Calculation --- */

/* Read literal values inserted by Tasker */
double p1 = %par1;
double max_steps = %AAB_AnimSteps;
double min_wait = %AAB_MinWait;
double max_wait = %AAB_MaxWait;

/* Clamp p1 just in case */
if (p1 < 0.0) p1 = 0.0;
if (p1 > 1.0) p1 = 1.0;

/* Compute loops and wait, rounding both to 0 digits (whole numbers) */
long loops = Math.round(1.0 + p1 * (max_steps - 1.0));
double raw_wait = (1.0 - p1) * (max_wait - min_wait) + min_wait;
long wait = Math.round(raw_wait);

/* Compute throttle, also rounding to a whole number */
long aabThrottle = Math.round(loops * wait + 10.0);

/* Add CycleTime if it exists and is <= 2000 */
String cycleTimeStr = tasker.getVariable("AAB_CycleTime");
if (cycleTimeStr != null) {
    try {
        double cycleTime = Double.parseDouble(cycleTimeStr);
        if (cycleTime <= 2000.0) {
            aabThrottle += Math.round(cycleTime);
        }
    } catch (NumberFormatException e) {
        /* Ignore invalid number format */
    }
}

/* --- CORRECTED SECTION --- */
/* Set the Tasker variables directly using the correct syntax */
tasker.setVariable("loops", String.valueOf(loops));
tasker.setVariable("wait", String.valueOf(wait));
tasker.setVariable("AAB_Throttle", String.valueOf(aabThrottle));

/* --- Java Code for Dynamic Threshold Calculation --- */

/* Read all necessary Tasker variables */
double par1 = %par1;
double SmoothedLux = %SmoothedLux;
double AAB_ThreshDim = %AAB_ThreshDim;
double AAB_ThreshBright = %AAB_ThreshBright;
double AAB_ThreshSteepness = %AAB_ThreshSteepness;
double AAB_ThreshMidpoint = %AAB_ThreshMidpoint;
double AAB_ThreshDark = %AAB_ThreshDark;
double AAB_Zone1End = %AAB_Zone1End;

/* A15 & A16: Calculate relative_change */
double lux_difference = Math.abs(par1 - SmoothedLux);
double relative_change = Math.round((lux_difference / (SmoothedLux + 1.0)) * 1000.0) / 1000.0;

/* A17: Calculate the sigmoid threshold for brighter light */
double log_lux = Math.log10(SmoothedLux + 1.0);
double exponent = -AAB_ThreshSteepness * (log_lux - AAB_ThreshMidpoint);
double thresh_sig = AAB_ThreshDim + (AAB_ThreshBright - AAB_ThreshDim) / (1.0 + Math.exp(exponent));
thresh_sig = Math.round(thresh_sig * 1000.0) / 1000.0;

/* A18: Calculate the linear threshold for dim light */
double thresh_low = AAB_ThreshDark - ((AAB_ThreshDark - AAB_ThreshDim) / AAB_Zone1End) * SmoothedLux;
thresh_low = Math.round(thresh_low * 1000.0) / 1000.0;

/* A19-A23: Choose the correct threshold based on the current lux */
double dynamic_threshold;
if (SmoothedLux < AAB_Zone1End) {
    dynamic_threshold = thresh_low;
} else {
    dynamic_threshold = thresh_sig;
}

/* --- MODIFIED SECTION --- */
/* Set the Tasker variables directly */
tasker.setVariable("relative_change", String.valueOf(relative_change));
tasker.setVariable("dynamic_threshold", String.valueOf(dynamic_threshold));

/* --- Java Code for Set Thresholds --- */

import java.math.BigDecimal;

/* Read all necessary input variables */
double par1 = %par1;
double par2 = %par2;
double LastRawLux = %AAB_LastRawLux;

String thresh_dynamic_str;
String thresh_low_str;
String thresh_high_str;

/* A2: If [ %par1 < 0.2 ] */
if (par1 < 0.2) {
    /* A3: Set hardcoded values */
    thresh_dynamic_str = "1";
    thresh_low_str = "0";
    thresh_high_str = "0.1";

} else {
    /* Pre-calculate the raw values since the formulas are the same */
    double dynamic_thresh_raw = par2 * 100.0;
    double thresh_abs_low_raw = LastRawLux * (1.0 - (dynamic_thresh_raw / 100.0));
    double thresh_abs_high_raw = LastRawLux * (1.0 + (dynamic_thresh_raw / 100.0));

    /* A4: Else If [ %par1 < 10 ] */
    if (par1 < 10) {
        /* A5: Round all results to 2 decimal places */
        BigDecimal dt_bd = new BigDecimal(dynamic_thresh_raw).setScale(2, BigDecimal.ROUND_HALF_UP);
        BigDecimal tal_bd = new BigDecimal(thresh_abs_low_raw).setScale(2, BigDecimal.ROUND_HALF_UP);
        BigDecimal tah_bd = new BigDecimal(thresh_abs_high_raw).setScale(2, BigDecimal.ROUND_HALF_UP);

        thresh_dynamic_str = dt_bd.toString();
        thresh_low_str = tal_bd.toString();
        thresh_high_str = tah_bd.toString();

    } else {
        /* A7: Round all results to 0 decimal places */
        BigDecimal dt_bd = new BigDecimal(dynamic_thresh_raw).setScale(0, BigDecimal.ROUND_HALF_UP);
        BigDecimal tal_bd = new BigDecimal(thresh_abs_low_raw).setScale(0, BigDecimal.ROUND_HALF_UP);
        BigDecimal tah_bd = new BigDecimal(thresh_abs_high_raw).setScale(0, BigDecimal.ROUND_HALF_UP);

        thresh_dynamic_str = dt_bd.toString();
        thresh_low_str = tal_bd.toString();
        thresh_high_str = tah_bd.toString();
    }
}

/* Set the Tasker global variables directly */
tasker.setVariable("AAB_ThreshDynamic", thresh_dynamic_str);
tasker.setVariable("AAB_ThreshAbsLow", thresh_low_str);
tasker.setVariable("AAB_ThreshAbsHigh", thresh_high_str);

/* --- Java Code for Initial Lux Setting --- */

/* Failsafe in case %as_values1 is not a valid number */
double raw_lux = 0.0;
try {
    /* Use direct assignment for the input */
    raw_lux = %as_values1;
} catch (Exception e) {
    /* If the variable is unset or invalid, raw_lux remains 0 */
}

/* Perform both rounding steps at once */
long smoothed_lux_0_digits = Math.round(raw_lux);
double smoothed_lux_2_digits = Math.round(raw_lux * 100.0) / 100.0;

/* --- MODIFIED SECTION --- */
/* Set the Tasker variables directly */
tasker.setVariable("smoothed_lux", String.valueOf(smoothed_lux_0_digits));
tasker.setVariable("SmoothedLux", String.valueOf(smoothed_lux_2_digits));

/* --- Java Code for Dynamic Range Compressed Scale (Modern) --- */

/* Read literal values inserted by Tasker */
double mappedBrightness = %par1;
double AAB_MinBright = %AAB_MinBright;
double AAB_ScaleDynamic = %AAB_ScaleDynamic;
double AAB_MaxBright = %AAB_MaxBright;
double AAB_Offset = %AAB_Offset;
double AAB_ScaleTaperMidpoint = %AAB_ScaleTaperMidpoint;
double AAB_ScaleTaperSteepness = %AAB_ScaleTaperSteepness;

/* Handle the special case where mappedBrightness is zero. */
if (mappedBrightness == 0.0) {
    double calculatedBrightness = AAB_MinBright;
    double scaleForGlobal = AAB_ScaleDynamic;

    /* Set the global variable directly */
    tasker.setVariable("AAB_ScaleDynamicCompress", String.valueOf(scaleForGlobal));

    /* Return only the primary result */
    return calculatedBrightness;

} else {
    /* Calculate the sigmoid-based compression factor and taper effect. */
    double midpoint = AAB_ScaleTaperMidpoint;
    double steepness = AAB_ScaleTaperSteepness;

    double exponent_raw = -steepness * (mappedBrightness - midpoint);
    double exponent = Math.round(exponent_raw * 1000.0) / 1000.0;

    double compression_factor_raw = 1.0 / (1.0 + Math.exp(exponent));
    double compression_factor = Math.round(compression_factor_raw * 1000.0) / 1000.0;

    double taper_effect_raw = 1.0 - compression_factor;
    double taper_effect = Math.round(taper_effect_raw * 1000.0) / 1000.0;

    /* Calculate the tapered scale and its dynamic limits (cap and floor). */
    double tapered_scale_raw = 1.0 + (AAB_ScaleDynamic - 1.0) * taper_effect;
    double tapered_scale = Math.round(tapered_scale_raw * 1000.0) / 1000.0;

    double dynamic_cap_raw = AAB_MaxBright / mappedBrightness;
    double dynamic_cap = Math.round(dynamic_cap_raw * 1000.0) / 1000.0;

    double dynamic_floor_raw = 2.0 - dynamic_cap;
    double dynamic_floor = Math.round(dynamic_floor_raw * 1000.0) / 1000.0;

    /* Apply the cap or floor to get the final effective_scale. */
    double effective_scale;
    if (AAB_ScaleDynamic > 1.0) {
        effective_scale = Math.min(tapered_scale, dynamic_cap);
    } else {
        effective_scale = Math.max(tapered_scale, dynamic_floor);
    }
    effective_scale = Math.round(effective_scale * 1000.0) / 1000.0;

    double scaleForGlobal = effective_scale;

    /* Calculate the final brightness and round to 1 decimal place. */
    double calculated_brightness_raw = mappedBrightness * effective_scale + AAB_Offset;
    double calculated_brightness = Math.round(calculated_brightness_raw * 10.0) / 10.0;

    /* --- MODIFIED SECTION --- */
    /* Set the global variable directly */
    tasker.setVariable("AAB_ScaleDynamicCompress", String.valueOf(scaleForGlobal));

    /* Return only the primary result */
    return calculated_brightness;
}

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* 1. Helper: Circular Range Check */
boolean isInRange(double t, double s, double e) {
    if (s < e) return t >= s && t < e;
    else return t >= s || t < e; // Wrapped
}

/* 2. Helper: Circular Progress Calculation */
double getProgress(double t, double s, double e) {
    double dur;
    boolean isWrapped = (s > e);

    if (isWrapped) dur = (86400.0 - s) + e;
    else dur = e - s;

    if (dur < 60.0) dur = 60.0; // Safety against zero division

    // If not in range, return -1 immediately
    if (!isInRange(t, s, e)) return -1.0;

    double dist;
    // Calculate distance from start 's'
    if (isWrapped && t < s) dist = (86400.0 - s) + t;
    else dist = t - s;

    return dist / dur;
}

/* 3. Inputs & Normalization */
// Parse configuration variables
double sim_spread = Double.parseDouble(tasker.getVariable("sim_spread"));
double sim_transition = Double.parseDouble(tasker.getVariable("sim_transition"));
double sim_steepness = Double.parseDouble(tasker.getVariable("sim_steepness"));
boolean isPolar = "true".equals(tasker.getVariable("AAB_PolarState"));

double sunlightDuration = 0.0;
try { 
    String sd = tasker.getVariable("AAB_Sunlightduration");
    if (sd != null) sunlightDuration = Double.parseDouble(sd); 
} catch (Exception e) {}

// Retrieve Raw Seconds from Tasker
// We use getVariable to ensure we get the string value, then parse
double raw_dawn = Double.parseDouble(tasker.getVariable("AAB_Sundawn"));
double raw_sunrise = Double.parseDouble(tasker.getVariable("AAB_Sunrise"));
double raw_noon = Double.parseDouble(tasker.getVariable("AAB_Sunnoon"));
double raw_sunset = Double.parseDouble(tasker.getVariable("AAB_Sunset"));
double raw_dusk = Double.parseDouble(tasker.getVariable("AAB_Sundusk"));

// Normalize Sun Events to 0-86400 range immediately
// This ensures all subsequent math works on a generic 24h clock
double sim_calc_dawn = ((raw_dawn % 86400) + 86400) % 86400;
double sim_calc_sunrise = ((raw_sunrise % 86400) + 86400) % 86400;
double sim_calc_noon = ((raw_noon % 86400) + 86400) % 86400;
double sim_calc_sunset = ((raw_sunset % 86400) + 86400) % 86400;
double sim_calc_dusk = ((raw_dusk % 86400) + 86400) % 86400;

/* 4. Calculate Windows (Absolute Durations) */
// FIX: Use Normalized values for Day Length to avoid date-stamp confusion
double aab_daylength = sim_calc_sunset - sim_calc_sunrise;
// If sunset is "before" sunrise (midnight crossing), add 24 hours
if (aab_daylength < 0) aab_daylength += 86400;

double delta_day = aab_daylength * sim_transition;
double aab_nightlength = 86400 - aab_daylength;
double delta_night = aab_nightlength * sim_transition;

// Calculate Starts/Ends with strict 0-86400 normalization
double sim_m_start = ((sim_calc_dawn - delta_night) % 86400 + 86400) % 86400;
double sim_m_end = ((sim_calc_sunrise + delta_day) % 86400 + 86400) % 86400;

double sim_e_start = ((sim_calc_sunset - delta_day) % 86400 + 86400) % 86400;
double sim_e_end = ((sim_calc_dusk + delta_night) % 86400 + 86400) % 86400;

/* 5. Main Loop */
List time_labels_list = new ArrayList();
List data_points_list = new ArrayList();

// Loop from 0 to 86400 (inclusive) to cover 00:00 to 24:00
for (int i = 0; i <= 86400; i += 600) {
    // FIX: Normalize 'now' for the calculation logic. 
    // This makes 86400 behave exactly like 0 for continuity checks.
    double now = (double) (i % 86400); 
    double progress = 0.0;

    if (isPolar) {
        progress = (sunlightDuration > 43200) ? 1.0 : 0.0; // Simple polar check (>12h)
    } else {
        double mProg = getProgress(now, sim_m_start, sim_m_end);
        double eProg = getProgress(now, sim_e_start, sim_e_end);

        if (mProg >= 0.0) {
            progress = mProg;
        } else if (eProg >= 0.0) {
            progress = 1.0 - eProg;
        } else {
            // Check "Day" state (Between Morning End and Evening Start)
            if (isInRange(now, sim_m_end, sim_e_start)) {
                progress = 1.0;
            } else {
                progress = 0.0;
            }
        }
    }

    // Clamp
    if (progress > 1.0) progress = 1.0;
    if (progress < 0.0) progress = 0.0;

    // Modifier Math (Sigmoid/Tanh smoothing)
    double x_factor = (progress - 0.5) * sim_steepness;
    double tanh_max = Math.tanh(sim_steepness / 2.0);
    double tanh_raw = Math.tanh(x_factor);
    double modifier = (Math.abs(tanh_max) > 1e-6) ? (tanh_raw / tanh_max) : 0.0;

    double scaled_value = 1.0 + (sim_spread / 100.0) * modifier;

    // Output Formatting
    data_points_list.add(new Double(scaled_value));

    int hour = i / 3600;
    if (hour == 24) hour = 0; // Handle 24:00 as 00:00 for label
    int minute = (i % 3600) / 60;
    time_labels_list.add(String.format("'%02d:%02d'", new Object[]{new Integer(hour), new Integer(minute)}));
}

/* 6. String Construction */
StringBuffer time_labels_str = new StringBuffer();
StringBuffer data_points_str = new StringBuffer();

for (int i = 0; i < time_labels_list.size(); i++) {
    if (i > 0) {
        time_labels_str.append(",");
        data_points_str.append(",");
    }
    time_labels_str.append(time_labels_list.get(i));
    data_points_str.append(new BigDecimal(((Double) data_points_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
}

/* 7. Return to Tasker */
// Set Local Variables for string replacements
tasker.setVariable("time_labels", time_labels_str.toString());
tasker.setVariable("data_points", data_points_str.toString());
tasker.setVariable("sim_calc_dawn", String.valueOf(sim_calc_dawn));
tasker.setVariable("sim_calc_noon", String.valueOf(sim_calc_noon));
tasker.setVariable("sim_calc_sunset", String.valueOf(sim_calc_sunset));
tasker.setVariable("sim_calc_dusk", String.valueOf(sim_calc_dusk));

/* --- Java Code for Sensor Event Pre-Processing (Corrected) --- */

import java.math.BigDecimal;

/* A1: Set the cycle start time */
/* Read TIMEMS as a string and parse it as a long to handle the large number. */
long now = Long.parseLong("%TIMEMS");
tasker.setVariable("AAB_CycleStart", String.valueOf(now));

/*
 * A2 & A3: Process the raw lux value (%as_values1).
 * The try-catch block replaces the "If %as_values1 Set" condition.
 */
try {
    String rawSensorValue = "%as_values1";

    /* A2: Strip potential brackets from the string */
    String cleanedValue = rawSensorValue.replace("[", "").replace("]", "");

    /* A3: Convert to a number, round it, and set %AAB_LastRawLux */
    double luxDouble = Double.parseDouble(cleanedValue);
    BigDecimal luxBd = new BigDecimal(luxDouble).setScale(3, BigDecimal.ROUND_HALF_UP);
    tasker.setVariable("AAB_LastRawLux", luxBd.toString());

    /* Also update %as_values1 itself with the cleaned value for the next task */
    tasker.setVariable("as_values1", cleanedValue);

} catch (Exception e) {
    /* If %as_values1 is not set or is not a valid number, this block is skipped. */
}

/*
 * A4: Process the sensor accuracy.
 * The try-catch block replaces the "If %as_accuracy Set" condition.
 */
try {
    String accuracy = "%as_accuracy";
    tasker.setVariable("AAB_LastSensorAccuracy", accuracy);
} catch (Exception e) {
    /* If %as_accuracy is not set, this block is skipped. */
}

/* --- Java Code for Dimming Curve Graph Generation --- */

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* --- Read all necessary Tasker variables --- */
double aab_minbright = %aab_minbright;
double aab_dimmingthreshold = %aab_dimmingthreshold;
double aab_dimmingexponent = %aab_dimmingexponent;
double aab_dimmingstrength = %aab_dimmingstrength;

/* A6-A10: Determine the max threshold for the loop */
double max_threshold = (aab_dimmingthreshold < 15) ? 15 : aab_dimmingthreshold;

double max_dim_strength = aab_minbright; // A11: Initialize

/* --- Initialize lists to hold our data points --- */
List brightness_labels_list = new ArrayList();
List dim_data_points_list = new ArrayList();
List ref_dim_data_points_list = new ArrayList();
List dim_ds_points_list = new ArrayList();

/* --- The main calculation loop (replicates A15-A34) --- */
/* The loop now runs from min bright to max threshold, incrementing by 1 */
for (double target_brightness = aab_minbright; target_brightness <= max_threshold; target_brightness++) {
    double dim_progress = 0.0;
    double ref_dim_progress = 0.0;

    /* A16-A20: Calculate dim_progress */
    if (target_brightness < aab_dimmingthreshold) {
        dim_progress = Math.pow(1.0 - ((target_brightness - aab_minbright) / (aab_dimmingthreshold - aab_minbright)), aab_dimmingexponent);
    } else {
        dim_progress = 0.0;
    }

    /* A21-A25: Calculate ref_dim_progress */
    if (target_brightness < 15) {
        ref_dim_progress = Math.pow(1.0 - (target_brightness / 15.0), 2.5);
    } else {
        ref_dim_progress = 0.0;
    }

    /* A26: Calculate dim_shell */
    double dim_shell = aab_dimmingstrength * dim_progress;

    /* A27-A29: Find the max_dim_strength at the first step */
    if (target_brightness == aab_minbright) {
        max_dim_strength = dim_shell;
    }

    /* Add the calculated points to our lists */
    brightness_labels_list.add(new Double(target_brightness));
    dim_data_points_list.add(new Double(dim_progress * 100.0));
    ref_dim_data_points_list.add(new Double(ref_dim_progress * 100.0));
    dim_ds_points_list.add(new Double(dim_shell));
}

/* --- Join the lists into comma-separated strings (replicates A35-A38) --- */
StringBuffer brightness_labels_str = new StringBuffer();
StringBuffer dim_data_points_str = new StringBuffer();
StringBuffer ref_dim_data_points_str = new StringBuffer();
StringBuffer dim_ds_points_str = new StringBuffer();

for (int i = 0; i < brightness_labels_list.size(); i++) {
    if (i > 0) {
        brightness_labels_str.append(",");
        dim_data_points_str.append(",");
        ref_dim_data_points_str.append(",");
        dim_ds_points_str.append(",");
    }
    brightness_labels_str.append(new BigDecimal(((Double) brightness_labels_list.get(i)).doubleValue()).setScale(0, BigDecimal.ROUND_HALF_UP).toString());
    dim_data_points_str.append(new BigDecimal(((Double) dim_data_points_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
    ref_dim_data_points_str.append(new BigDecimal(((Double) ref_dim_data_points_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
    dim_ds_points_str.append(new BigDecimal(((Double) dim_ds_points_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP).toString());
}

/* --- Set all the variables Tasker needs for the HTML replacement --- */
tasker.setVariable("brightness_labels", brightness_labels_str.toString());
tasker.setVariable("dim_data_points", dim_data_points_str.toString());
tasker.setVariable("ref_dim_data_points", ref_dim_data_points_str.toString());
tasker.setVariable("dim_ds_points", dim_ds_points_str.toString());
tasker.setVariable("max_dim_strength", String.valueOf(max_dim_strength));

/* --- Java Code for Alpha Graph Generation --- */

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* --- Read all necessary Tasker variables --- */
double aab_deltafactor = %aab_deltafactor;

/* --- The hardcoded label values from the For loop --- */
double[] labelValues = {
    1,1.22,1.49,1.82,2.23,2.72,3.32,4.06,4.95,6.05,7.39,9.03,11.03,13.47,16.45,20.09,24.54,29.98,36.61,44.72,54.62,66.72,81.49,99.54,121.58,148.5,181.38,221.55,270.61,330.53,403.72,493.11,602.3,735.67,898.57,1097.55,1340.58,1637.42,2000
};

/* --- Initialize lists to hold our data points --- */
List lux_labels_list = new ArrayList();
List new_data_list = new ArrayList();
List ref_data_list = new ArrayList();

/* --- The main calculation loop (replicates A8-A15) --- */
for (int i = 0; i < labelValues.length; i++) {
    double label = labelValues[i];

    /* A9: Calculate lux_delta */
    double lux_delta = label / 100.0;

    /* A10: Calculate new_point */
    double new_point = 1.0 - Math.exp(-aab_deltafactor * lux_delta);

    /* A11: Calculate ref_point */
    double ref_point = 1.0 - Math.exp(-1.8 * lux_delta);

    /* Add the calculated points to our lists */
    lux_labels_list.add(new Double(label));
    new_data_list.add(new Double(new_point));
    ref_data_list.add(new Double(ref_point));
}

/* --- Join the lists into comma-separated strings (replicates A16-A18) --- */
StringBuffer lux_labels_str = new StringBuffer();
StringBuffer new_data_str = new StringBuffer();
StringBuffer ref_data_str = new StringBuffer();

for (int i = 0; i < lux_labels_list.size(); i++) {
    if (i > 0) {
        lux_labels_str.append(",");
        new_data_str.append(",");
        ref_data_str.append(",");
    }
    lux_labels_str.append(lux_labels_list.get(i));
    /* Round the data points for clean output */
    new_data_str.append(new BigDecimal(((Double) new_data_list.get(i)).doubleValue()).setScale(6, BigDecimal.ROUND_HALF_UP).toString());
    ref_data_str.append(new BigDecimal(((Double) ref_data_list.get(i)).doubleValue()).setScale(6, BigDecimal.ROUND_HALF_UP).toString());
}

/* --- Set all the variables Tasker needs for the HTML replacement --- */
tasker.setVariable("lux_labels", lux_labels_str.toString());
tasker.setVariable("new_data", new_data_str.toString());
tasker.setVariable("ref_data", ref_data_str.toString());

import android.provider.Settings;
import android.content.Intent;
import android.net.Uri;
import android.app.AlertDialog;
import android.content.DialogInterface;
import android.app.Activity;
import android.app.NotificationManager;
import android.app.AppOpsManager;
import android.content.Context;
import android.content.pm.PackageManager;
import android.Manifest;
import android.os.Build;
import java.util.function.Consumer;
import io.reactivex.subjects.SingleSubject;

/* 1. Setup & Surgical Fix for Package Detection */
sdk = Build.VERSION.SDK_INT;

/* 
 * SURGICAL FIX: 
 * Instead of using 'App Info', we ask the context for its own package name.
 * This works inside Tasker (returns net.dinglisch...) and in the Exported App (returns com.tideo.aab...).
 */
targetPkg = context.getPackageName();

/* Set the legacy variable as requested so other tasks can use it if needed */
tasker.setVariable("AAB_Package", targetPkg);

nm = (NotificationManager) context.getSystemService(Context.NOTIFICATION_SERVICE);
pm = context.getPackageManager();
appOps = (AppOpsManager) context.getSystemService(Context.APP_OPS_SERVICE);

/* State Tracking */
forceRestrictedFix = false;

/* MAIN LOOP */
while (true) {

    /* 2. Check Permissions */

    /* System Settings */
    if (Settings.System.canWrite(context)) {
        hasWrite = true;
    } else {
        hasWrite = false;
    }

    /* Overlay: Use AppOps for accurate package checking */
    try {
        uid = pm.getPackageUid(targetPkg, 0);
        opMode = appOps.checkOpNoThrow("android:system_alert_window", uid, targetPkg);
        /* MODE_ALLOWED = 0 */
        if (opMode == 0) {
            hasOverlay = true;
        } else {
            hasOverlay = false;
        }
    } catch (Exception e) {
        if (Settings.canDrawOverlays(context)) {
            hasOverlay = true;
        } else {
            hasOverlay = false;
        }
    }

    if (nm.areNotificationsEnabled()) {
        hasNotify = true;
    } else {
        hasNotify = false;
    }

    locPerm = pm.checkPermission(Manifest.permission.ACCESS_FINE_LOCATION, targetPkg);
    if (locPerm == PackageManager.PERMISSION_GRANTED) {
        hasLoc = true;
    } else {
        hasLoc = false;
    }

    /* Background Location */
    if (hasLoc && sdk >= 29) {
        bgLocPerm = pm.checkPermission(Manifest.permission.ACCESS_BACKGROUND_LOCATION, targetPkg);
        if (bgLocPerm != PackageManager.PERMISSION_GRANTED) {
            hasLoc = false;
        }
    }

    /* 3. Exit Condition */
    if (hasWrite && hasOverlay && hasNotify && hasLoc) {
        tasker.setVariable("AAB_PermGranted", "3");
        return "Success";
    }

    /* 4. Determine NEXT step */
    nextAction = "";
    title = "";
    message = "";
    /* 1=Write, 2=Overlay, 3=Loc, 4=Notify, 5=RestrictedFix */
    checkType = 0; 

    if (!hasWrite) {
        title = "Step 1: Write Settings";
        message = "Required to control brightness.\n\n1. Tap 'Grant'.\n2. Enable the switch.\n3. Press Back.";
        nextAction = Settings.ACTION_MANAGE_WRITE_SETTINGS;
        checkType = 1;
    } else if (!hasOverlay) {

        if (forceRestrictedFix) {
            title = "Restricted Settings Fix";
            message = "1. Tap 'Fix' (opens App Info).\n2. Tap 3-dots (top right) -> 'Allow restricted settings'.\n3. Press Back.\n\nWe will then send you to the switch again.";
            nextAction = Settings.ACTION_APPLICATION_DETAILS_SETTINGS;
            checkType = 5; 
        } else {
            title = "Step 2: Display Overlay";
            message = "Required for the dimming filter.\n\n1. Tap 'Grant'.\n2. Enable the switch.\n\n❓ IF GREYED OUT: Tap Grant. CRUCIAL: attempt to grant the permission anyway even if it is greyed out. Lastly, press Back. We will ask if you need help.";
            nextAction = Settings.ACTION_MANAGE_OVERLAY_PERMISSION;
            checkType = 2;
        }

    } else if (!hasLoc) {
        title = "Step 3: Location";
        message = "Required for sunrise/sunset.\n\n1. Tap 'Grant'.\n2. Tap 'Permissions' > 'Location'.\n3. Select 'Allow all the time'.";
        nextAction = Settings.ACTION_APPLICATION_DETAILS_SETTINGS;
        checkType = 3;
    } else {
        title = "Step 4: Notifications";
        message = "Required for service stability.\n\n1. Tap 'Grant'.\n2. Enable notifications.";
        if (sdk >= 26) {
            nextAction = Settings.ACTION_APP_NOTIFICATION_SETTINGS;
        } else {
            nextAction = Settings.ACTION_APPLICATION_DETAILS_SETTINGS;
        }
        checkType = 4;
    }

    /* 5. Show Dialog (Blocking) */
    dialogSignal = SingleSubject.create();

    uiConsumer = new Consumer() {
        accept(Object activityObj) {
            try {
                Activity act = (Activity) activityObj;
                AlertDialog.Builder builder = new AlertDialog.Builder(act);
                builder.setTitle(title);
                builder.setMessage(message);
                builder.setCancelable(false);

                String btnText = "Grant";
                if (checkType == 5) {
                    btnText = "Fix";
                }

                builder.setPositiveButton(btnText, new DialogInterface.OnClickListener() {
                    onClick(DialogInterface dialog, int which) {
                        dialogSignal.onSuccess("GRANT");
                        act.finish();
                    }
                });
                builder.setNegativeButton("Cancel", new DialogInterface.OnClickListener() {
                    onClick(DialogInterface dialog, int which) {
                        dialogSignal.onSuccess("CANCEL");
                        act.finish();
                    }
                });
                builder.show();
            } catch (Exception e) {
                dialogSignal.onSuccess("ERROR");
                if (activityObj instanceof Activity) {
                    ((Activity) activityObj).finish();
                }
            }
        }
    };

    tasker.doWithActivity(uiConsumer);
    userChoice = dialogSignal.blockingGet();

    if ("CANCEL".equals(userChoice)) return "Cancelled";
    if ("ERROR".equals(userChoice)) return "Error";

    /* 6. Launch Intent */
    try {
        intent = new Intent(nextAction);
        if (checkType == 4 && sdk >= 26) {
             intent.putExtra(Settings.EXTRA_APP_PACKAGE, targetPkg);
             intent.putExtra("android.provider.extra.APP_PACKAGE", targetPkg);
        } else {
             intent.setData(Uri.parse("package:" + targetPkg));
        }
        tasker.getWithActivityForResult(intent).blockingGet();
    } catch (Exception e) {
        tasker.showToast("Could not open settings page.");
        try { Thread.sleep(1000); } catch (Exception s) {}
    }

    /* 7. Logic Reset & Verification */

    /* CRITICAL FIX: If we just came from the Restricted Fix page (Type 5), 
       turn off the flag so the next loop sends the user back to the standard Overlay page (Type 2). */
    if (checkType == 5) {
        forceRestrictedFix = false;
        /* Give user a moment to breathe before the next dialog */
        try { Thread.sleep(500); } catch (Exception s) {}
        continue; /* Skip the troubleshooting check below, just restart loop */
    }

    try { Thread.sleep(500); } catch (Exception s) {}

    /* 8. Troubleshooting: Only run if we just came from the Overlay screen (Type 2) */
    if (checkType == 2) {
        /* Re-check specifically for Overlay */
        stillMissing = false;
        try {
            uid = pm.getPackageUid(targetPkg, 0);
            opMode = appOps.checkOpNoThrow("android:system_alert_window", uid, targetPkg);
            if (opMode != 0) stillMissing = true;
        } catch (Exception e) {
            if (!Settings.canDrawOverlays(context)) stillMissing = true;
        }

        if (stillMissing) {
            /* Show Trouble Dialog */
            troubleSignal = SingleSubject.create();

            troubleConsumer = new Consumer() {
                accept(Object activityObj) {
                    Activity act = (Activity) activityObj;
                    AlertDialog.Builder builder = new AlertDialog.Builder(act);
                    builder.setTitle("Permission Not Detected");
                    builder.setMessage("The overlay permission is still off.\n\nWas the switch GREYED OUT (Disabled)?");
                    builder.setCancelable(false);

                    builder.setPositiveButton("Yes (Restricted)", new DialogInterface.OnClickListener() {
                        onClick(DialogInterface dialog, int which) {
                            troubleSignal.onSuccess("RESTRICTED");
                            act.finish();
                        }
                    });

                    builder.setNegativeButton("No (Retry)", new DialogInterface.OnClickListener() {
                        onClick(DialogInterface dialog, int which) {
                            troubleSignal.onSuccess("RETRY");
                            act.finish();
                        }
                    });
                    builder.show();
                }
            };

            tasker.doWithActivity(troubleConsumer);
            tChoice = troubleSignal.blockingGet();

            if ("RESTRICTED".equals(tChoice)) {
                forceRestrictedFix = true; /* Next loop will be Type 5 (App Info) */
            } else {
                forceRestrictedFix = false; /* Next loop will be Type 2 (Overlay) again */
            }
        }
    }
}

/*
AAB Curve Fitting Engine V40.3 (Robustness Pack)
- Fix 1: safePowDelta uses a 1e-9 floor instead of 0.0 to preserve gradients.
- Fix 2: Global weight normalization ensures consistent error scales.
- Fix 3: Inertia Regularization (Lambda) prevents overfitting in sparse zones.
- Update: Tau is now dynamic (Tasker var %tau), defaults to 4.0.
*/

import java.util.ArrayList;
import java.util.List;
import java.util.Collections;
import java.util.Comparator;

logBuffer = new StringBuffer();
suggestion_made = false;

/* FIX 1: Safe Power with Floor (Preserve Gradient) */
double safePowDelta(double val, double p) {
    /* Never return 0.0 or NaN; clamp to epsilon to keep solver "alive" */
    double delta = Math.max(val, 1e-9);
    return Math.pow(delta, p);
}

/* Helper: Log-Space Weighting (Inverse Lux) with Special Ghost Handling */
double getLogWeight(double[] pt) {
    if (pt == null || pt.length < 3) return 0.1;

    // Check for Ghost Point (Source ID = 3.0 at index 3)
    if (pt.length > 3 && Math.abs(pt[3] - 3.0) < 0.1) {
        /* Ghosts are fixed anchors, scaled to be comparable to mid-range real points */
        return pt[2] * 0.01;
    }

    // Real Data: Inverse Lux weighting (1/L)
    return pt[2] / (pt[0] + 2.0);
}

/* Helper: Calculate adaptive number of refinement points */
int calculateRefinePoints(double gap_start, double gap_end, double current_pos) {
    double gap_ratio, gap_size;
    boolean is_dark_zone;
    if (gap_start <= 1e-9) return 5;
    gap_ratio = gap_end / gap_start;
    gap_size = gap_end - gap_start;
    is_dark_zone = (current_pos < 100.0);

    if (gap_ratio < 1.5 || gap_size < 10.0) return 0;
    if (gap_ratio < 3.0) return is_dark_zone ? 3 : 5;
    return is_dark_zone ? 5 : 8;
}

/* Helper: Generate log-spaced points */
List generateSmartRefinementPoints(double start, double end, double current, int count) {
    List points = new ArrayList();
    double log_start, log_end, log_current, total_span, left_span, right_span;
    int left_points, right_points, i;
    double left_step, right_step, val;

    if (count <= 0) { points.add(new Double(current)); return points; }
    if (start <= 1e-9 || end <= 1e-9 || start >= end || current < start || current > end) {
        points.add(new Double(current)); return points;
    }

    log_start = Math.log(start); log_end = Math.log(end); log_current = Math.log(current);
    total_span = log_end - log_start;

    if (total_span < 1e-9) { points.add(new Double(current)); return points; }

    left_span = log_current - log_start; right_span = log_end - log_current;
    left_points = (int)Math.round(count * (left_span / total_span));
    right_points = count - left_points;

    if (left_points > 0 && left_span > 1e-9) {
        left_step = left_span / (left_points + 1);
        for (i = 1; i <= left_points; i++) {
            val = Math.exp(log_start + i * left_step);
            points.add(new Double(val));
        }
    }
    points.add(new Double(current));
    if (right_points > 0 && right_span > 1e-9) {
        right_step = right_span / (right_points + 1);
        for (i = 1; i <= right_points; i++) {
            val = Math.exp(log_current + i * right_step);
            points.add(new Double(val));
        }
    }
    return points;
}

/* Helper: Zone 1 (sqrt) Weighted fit */
double[] fitZone1(List points) {
    double[] result = {0.0, -2.0};
    int i; double sum_wy = 0.0, sum_w = 0.0, mean_y_w, sum_w_xy = 0.0, sum_w_xx = 0.0, param_a, ss_total_w = 0.0, ss_residual_w = 0.0;
    double x, y, w, x_prime, y_pred; double[] pt;

    if (points.size() < 3) return result;

    for (i = 0; i < points.size(); i++) {
        pt = (double[]) points.get(i); w = getLogWeight(pt);
        sum_wy += w * pt[1]; sum_w += w;
    }
    mean_y_w = (sum_w > 1e-9) ? (sum_wy / sum_w) : 0.0;

    for (i = 0; i < points.size(); i++) {
        pt = (double[]) points.get(i);
        x = pt[0]; y = pt[1]; w = getLogWeight(pt);
        x_prime = Math.sqrt(x);
        sum_w_xy += w * x_prime * y; sum_w_xx += w * x_prime * x_prime;
    }

    param_a = (sum_w_xx > 1e-9) ? (sum_w_xy / sum_w_xx) : 0.0;
    result[0] = param_a;

    for (i = 0; i < points.size(); i++) {
        pt = (double[]) points.get(i);
        x = pt[0]; y = pt[1]; w = getLogWeight(pt);
        y_pred = param_a * Math.sqrt(x);
        ss_total_w += w * Math.pow(y - mean_y_w, 2.0);
        ss_residual_w += w * Math.pow(y - y_pred, 2.0);
    }
    if (ss_total_w < 1e-9) result[1] = 0.0; else result[1] = 1.0 - (ss_residual_w / ss_total_w);
    return result;
}

/* Helper: Zone 2 Weighted R²-only fit */
double getR2_Z2_only(List points, double form2c, double form2d, double start_a, double start_b) {
    int i; double sum_wy_valid = 0.0, sum_w_valid = 0.0, mean_y_valid, a, b, term, term_d, w, base_d;
    double ss_total_w = 0.0, ss_residual_w = 0.0, y_pred, sum_num = 0.0, sum_den = 0.0;
    double[] pt; List valid_points = new ArrayList();

    if (points.size() < 3) return -2.0;
    if (form2c < -50.0) form2c = -50.0;

    base_d = form2d - form2c; if (base_d < 1e-9) base_d = 1e-9;
    term_d = safePowDelta(base_d, 0.33);

    for (i = 0; i < points.size(); i++) {
        pt = (double[]) points.get(i);
        if (pt[0] > form2c && form2d > form2c) {
            valid_points.add(pt); w = getLogWeight(pt);
            sum_wy_valid += w * pt[1]; sum_w_valid += w;
        }
    }

    if (valid_points.size() < 3) return -2.0;
    mean_y_valid = (sum_w_valid > 1e-9) ? (sum_wy_valid / sum_w_valid) : 0.0;

    a = start_a; 
    for (i = 0; i < valid_points.size(); i++) {
        pt = (double[]) valid_points.get(i); w = getLogWeight(pt);
        term = safePowDelta(pt[0] - form2c, 0.33) - term_d;
        sum_num += w * (pt[1] - a) * term; sum_den += w * term * term;
    }
    if (sum_den > 1e-9) b = sum_num / sum_den; else b = start_b;
    if (b < 0.0) b = 0.01;

    for (i = 0; i < valid_points.size(); i++) {
        pt = (double[]) valid_points.get(i); w = getLogWeight(pt);
        term = safePowDelta(pt[0] - form2c, 0.33) - term_d;
        y_pred = a + b * term;
        ss_total_w += w * Math.pow(pt[1] - mean_y_valid, 2.0);
        ss_residual_w += w * Math.pow(pt[1] - y_pred, 2.0);
    }
    if (ss_total_w < 1e-9) return 0.0;
    return 1.0 - (ss_residual_w / ss_total_w);
}

/* Helper: Zone 3 Weighted R²-only fit */
double[] fitZone3(List points, double max_bright) {
    int i; double sum_wy = 0.0, sum_w = 0.0, mean_y_w, sum_w_xy = 0.0, sum_w_xx = 0.0, param_a, ss_total_w = 0.0, ss_residual_w = 0.0;
    double x, y, w, x_prime, y_prime, y_pred; double[] pt; List work_points = new ArrayList();
    double[] result = {0.0, -2.0};

    if (points.size() == 0) return result;
    for (i = 0; i < points.size(); i++) { pt = (double[]) points.get(i); if (pt[0] > 10.0) work_points.add(pt); }
    if (work_points.size() < 3) { if (points.size() >= 3) work_points = points; else return result; }

    for (i = 0; i < work_points.size(); i++) {
        pt = (double[]) work_points.get(i); w = getLogWeight(pt);
        sum_wy += w * pt[1]; sum_w += w;
    }
    mean_y_w = (sum_w > 1e-9) ? (sum_wy / sum_w) : 0.0;

    for (i = 0; i < work_points.size(); i++) {
        pt = (double[]) work_points.get(i); x = pt[0]; y = pt[1]; w = getLogWeight(pt);
        x_prime = 1.0 / x; y_prime = (max_bright - y) / max_bright;
        sum_w_xy += w * x_prime * y_prime; sum_w_xx += w * x_prime * x_prime;
    }

    param_a = (sum_w_xx > 1e-9) ? (sum_w_xy / sum_w_xx) : 0.0;
    result[0] = param_a;

    for (i = 0; i < work_points.size(); i++) {
        pt = (double[]) work_points.get(i); x = pt[0]; y = pt[1]; w = getLogWeight(pt);
        ss_total_w += w * Math.pow(y - mean_y_w, 2.0);
        y_pred = max_bright - (param_a / x) * max_bright;
        ss_residual_w += w * Math.pow(y - y_pred, 2.0);
    }
    if (ss_total_w < 1e-9) result[1] = 0.0; else result[1] = 1.0 - (ss_residual_w / ss_total_w);
    return result;
}

/* Fast Error/Bias Helpers */
double fastZone1Error(List points, double form1a, double max_bright) {
    if (points.size() == 0 || max_bright < 1e-9) return 0.0;
    double sum_sq_w = 0.0, sum_w = 0.0, e1, w; int j; double[] pt1;
    for (j = 0; j < points.size(); j++) {
        pt1 = (double[]) points.get(j); w = getLogWeight(pt1);
        e1 = (form1a * Math.sqrt(pt1[0])) - pt1[1];
        sum_sq_w += w * e1 * e1; sum_w += w;
    }
    if (sum_w < 1e-9) return 0.0;
    return Math.sqrt(sum_sq_w / sum_w) / max_bright;
}
double fastZone1Bias(List points, double form1a) {
    if (points.size() == 0) return 0.0;
    double sum_err_w = 0.0, sum_w = 0.0, e1, w; int j; double[] pt1;
    for (j = 0; j < points.size(); j++) {
        pt1 = (double[]) points.get(j); w = getLogWeight(pt1);
        e1 = (form1a * Math.sqrt(pt1[0])) - pt1[1];
        sum_err_w += w * e1; sum_w += w;
    }
    if (sum_w < 1e-9) return 0.0; return sum_err_w / sum_w;
}
double fastZone2Error(List points, double form2a, double form2b, double form2c, double z1e, double max_bright) {
    if (max_bright < 1e-9) return 0.0;
    double sum_sq2_w = 0.0, sum_w2 = 0.0, w; int count_valid = 0; int j; double[] pt2m; double x2, term_x2, term_d2, y_pred2, e2;
    for (j = 0; j < points.size(); j++) {
        pt2m = (double[]) points.get(j); x2 = pt2m[0]; if (x2 <= form2c) continue;
        w = getLogWeight(pt2m);
        term_x2 = safePowDelta(x2 - form2c, 0.33); term_d2 = safePowDelta(z1e - form2c, 0.33);
        y_pred2 = form2a + form2b * (term_x2 - term_d2);
        e2 = y_pred2 - pt2m[1]; sum_sq2_w += w * e2 * e2; sum_w2 += w; count_valid++;
    }
    if (count_valid == 0 || sum_w2 < 1e-9) return 0.0;
    return Math.sqrt(sum_sq2_w / sum_w2) / max_bright;
}
double fastZone2Bias(List points, double form2a, double form2b, double form2c, double z1e) {
    double sum_err2_w = 0.0, sum_w2 = 0.0, w; int count_valid = 0; int j; double[] pt2m; double x2, term_x2, term_d2, y_pred2, e2;
    for (j = 0; j < points.size(); j++) {
        pt2m = (double[]) points.get(j); x2 = pt2m[0]; if (x2 <= form2c) continue;
        w = getLogWeight(pt2m);
        term_x2 = safePowDelta(x2 - form2c, 0.33); term_d2 = safePowDelta(z1e - form2c, 0.33);
        y_pred2 = form2a + form2b * (term_x2 - term_d2);
        e2 = y_pred2 - pt2m[1]; sum_err2_w += w * e2; sum_w2 += w; count_valid++;
    }
    if (count_valid == 0 || sum_w2 < 1e-9) return 0.0; return sum_err2_w / sum_w2;
}
double fastZone3Error(List points, double form3a, double max_bright) {
    if (points.size() == 0 || max_bright < 1e-9) return 0.0;
    double sum_sq3_w = 0.0, sum_w3 = 0.0, e3, w; int j; double[] pt3m;
    for (j = 0; j < points.size(); j++) {
        pt3m = (double[]) points.get(j); w = getLogWeight(pt3m);
        e3 = (max_bright - (form3a / pt3m[0]) * max_bright) - pt3m[1];
        sum_sq3_w += w * e3 * e3; sum_w3 += w;
    }
    if (sum_w3 < 1e-9) return 0.0;
    return Math.sqrt(sum_sq3_w / sum_w3) / max_bright;
}
double fastZone3Bias(List points, double form3a, double max_bright) {
    if (points.size() == 0) return 0.0;
    double sum_err3_w = 0.0, sum_w3 = 0.0, e3, w; int j; double[] pt3m;
    for (j = 0; j < points.size(); j++) {
        pt3m = (double[]) points.get(j); w = getLogWeight(pt3m);
        e3 = (max_bright - (form3a / pt3m[0]) * max_bright) - pt3m[1];
        sum_err3_w += w * e3; sum_w3 += w;
    }
    if (sum_w3 < 1e-9) return 0.0; return sum_err3_w / sum_w3;
}
double calculateSizePenalty(int count) {
    if (count >= 4) return 0.0;
    double diff = 4.0 - count; return 0.25 * diff * diff; 
}

/* Helper: Calculate blended point count based on quality (R² + nRMSE) */
double getWeightedCount(int count, double r2, double nrmse) {
    if (count <= 0) return 0.0;
    double r2_safe = (r2 < 0.0) ? 0.0 : r2;
    if (r2_safe > 1.0) r2_safe = 1.0;

    /* nRMSE penalty: Reduce quality if error is high, capped at 0.5 to keep points relevant */
    double err_pen = (nrmse > 0.5) ? 0.5 : nrmse;
    double quality = r2_safe * (1.0 - err_pen);

    /* Weight Mix: 25% Pure Count, 75% Quality 
       3 pts @ 0.95 R² -> 3 * (0.25 + 0.71) ≈ 2.88
       4 pts @ 0.60 R² -> 4 * (0.25 + 0.45) = 2.80 
       (3 High Quality > 4 Low Quality) */
    double weight = 0.25 + 0.75 * quality;
    return count * weight;
}

/* Helper: Analytic Approximation Cost (Weighted) */
double approximateCost(double z1e, double z2e, List dataPoints, int N, double form1a, double old_form2b, double form2c, double max_bright) {
    List z1_pts = new ArrayList(); List z2_pts = new ArrayList(); List z3_pts = new ArrayList();
    int i; double[] pt; 
    double boundary_y2_end, approx_form2a, approx_form3a, z1_nrmse, z2_nrmse, z3_nrmse, z1_bias, z2_bias, z3_bias;
    double sum_num, sum_den, term_x, term_d, best_b, x, w, base_d;

    if (z1e >= z2e) return 1e12; // Safety check

    for (i = 0; i < N; i++) {
        pt = (double[]) dataPoints.get(i);
        if (pt[0] <= z1e) z1_pts.add(pt);
        else if (pt[0] <= z2e) z2_pts.add(pt);
        else z3_pts.add(pt);
    }

    if (z1_pts.size() < 3 || z2_pts.size() < 3) return 1e12;
    if (z3_pts.size() == 0 && N < 12) return 1e12;

    approx_form2a = form1a * Math.sqrt(z1e);
    base_d = z1e - form2c; if (base_d < 1e-9) base_d = 1e-9;
    term_d = safePowDelta(base_d, 0.33);

    sum_num = 0.0; sum_den = 0.0;
    for (i=0; i<z2_pts.size(); i++) {
        pt = (double[]) z2_pts.get(i); x = pt[0]; w = getLogWeight(pt);
        if (x <= form2c) continue;
        term_x = safePowDelta(x - form2c, 0.33) - term_d;
        sum_num += w * (pt[1] - approx_form2a) * term_x;
        sum_den += w * term_x * term_x;
    }
    if (sum_den > 1e-9) best_b = sum_num / sum_den; else best_b = old_form2b;
    if (best_b < 0.0) best_b = 0.01;

    base_d = z2e - form2c; if(base_d < 1e-9) base_d = 1e-9;
    boundary_y2_end = approx_form2a + best_b * (safePowDelta(base_d, 0.33) - term_d);

    approx_form3a = (max_bright > 0.01) ? (z2e * (max_bright - boundary_y2_end) / max_bright) : 0.0;
    if (approx_form3a < 0.0) approx_form3a = 0.0;

    z1_nrmse = fastZone1Error(z1_pts, form1a, max_bright);
    z2_nrmse = fastZone2Error(z2_pts, approx_form2a, best_b, form2c, z1e, max_bright);
    z1_bias = fastZone1Bias(z1_pts, form1a);
    z2_bias = fastZone2Bias(z2_pts, approx_form2a, best_b, form2c, z1e);

    if (z3_pts.size() >= 2) {
        z3_nrmse = fastZone3Error(z3_pts, approx_form3a, max_bright);
        z3_bias = fastZone3Bias(z3_pts, approx_form3a, max_bright);
    } else { z3_nrmse = 0.0; z3_bias = 0.0; }

    double size_p = calculateSizePenalty(z1_pts.size()) + calculateSizePenalty(z2_pts.size()) + calculateSizePenalty(z3_pts.size());
    double reg_p = 0.001 * best_b * best_b + 0.0005 * Math.abs(form2c);

    return z1_nrmse + z2_nrmse + z3_nrmse + (Math.abs(z1_bias) + Math.abs(z2_bias) + Math.abs(z3_bias)) + size_p + reg_p;
}

/* Helper: Full fit and cost calculation (Weighted) */
double[] calculateFitAndCost(double z1e, double z2e, List dataPoints, int N, double current_form2b, double current_form2c, double max_bright) {
    double[] results = new double[18];
    int i, j, iter, max_iter, valid_z2_pts;
    double cand_form2a, cand_form2b, cand_form2c, cand_form2d, cand_r2_z2, MB;
    double cand_form1a, cand_r2_z1, b, c, form2d;
    double x, y_actual, term_x, term_d, y_predicted, error, d_term_c, base, base_d;
    double boundary_y2_end, cand_form3a, cand_r2_z3, w;
    double z1_nrmse, z1_bias, z2_nrmse, z2_bias, z3_nrmse, z3_bias, size_penalty, cost, r2_penalty, reg_penalty;
    double[] fitZ1_final, pt, pt1, pt2m, pt3m;
    double sum_sq, sum_err, sum_sq2, sum_err2, sum_sq3, sum_err3, sum_w, sum_w2, sum_w3;
    double y_pred1, e1, x2, term_x2, term_d2, y_pred2, e2, y_pred3m, e3;
    double sum_num, sum_den, err_c, lr_c;
    List z1_pts, z2_pts, z3_pts;
    double sum_wy_z3, sum_w_z3, mean_y_z3, ss_total_z3, ss_res_z3;
    double prev_c;

    for (i=0; i<18; i++) results[i] = 0.0;
    results[0] = 1e12; 

    if (z1e >= z2e || z1e < 0) return results; // Return error state

    z1_pts = new ArrayList(N); z2_pts = new ArrayList(N); z3_pts = new ArrayList(N);
    for (i = 0; i < N; i++) {
        pt = (double[]) dataPoints.get(i); 
        if (pt[0] <= z1e) z1_pts.add(pt);
        else if (pt[0] <= z2e) z2_pts.add(pt);
        else z3_pts.add(pt);
    }

    if (z1_pts.size() < 3 || z2_pts.size() < 3) return results;

    fitZ1_final = fitZone1(z1_pts); cand_form1a = fitZ1_final[0]; cand_r2_z1 = fitZ1_final[1];

    b = current_form2b; c = current_form2c; if (c < -50.0) c = -50.0;
    cand_form2a = cand_form1a * Math.sqrt(z1e); form2d = z1e;

    max_iter = 100; lr_c = 0.2; prev_c = c;

    for (iter = 0; iter < max_iter; iter++) {
        sum_num = 0.0; sum_den = 0.0; valid_z2_pts = 0; sum_w2 = 0.0;
        base_d = form2d - c; if (base_d < 1e-4) base_d = 1e-4; term_d = safePowDelta(base_d, 0.33);

        for (i = 0; i < z2_pts.size(); i++) {
            pt = (double[]) z2_pts.get(i); w = getLogWeight(pt);
            if (pt[0] <= c || form2d <= c) continue;
            term_x = safePowDelta(pt[0] - c, 0.33) - term_d;
            sum_num += w * (pt[1] - cand_form2a) * term_x; sum_den += w * term_x * term_x;
            valid_z2_pts++; sum_w2 += w;
        }

        if (valid_z2_pts < 3) break;
        if (sum_den > 1e-9) b = sum_num / sum_den;
        if (b < 0.0) b = 0.01;

        err_c = 0.0;
        for (i = 0; i < z2_pts.size(); i++) {
            pt = (double[]) z2_pts.get(i); x = pt[0]; y_actual = pt[1]; w = getLogWeight(pt);
            if (x <= c || form2d <= c) continue;
            term_x = safePowDelta(x - c, 0.33); 
            y_predicted = cand_form2a + b * (term_x - term_d);
            error = y_predicted - y_actual;
            base = x - c; if (base < 1e-4) base = 1e-4;
            d_term_c = (0.33 * b) * (Math.pow(base_d, -0.67) - Math.pow(base, -0.67));
            err_c += w * error * d_term_c;
        }
        if (sum_w2 > 1e-9) c -= (lr_c * err_c) / sum_w2;
        c = Math.min(c, form2d - 1e-4); if (c < -50.0) c = -50.0;
        if (Math.abs(c - prev_c) < 0.002) break;
        prev_c = c; lr_c *= 0.95;
    }

    cand_form2b = b; cand_form2c = c; cand_form2d = form2d;
    cand_r2_z2 = getR2_Z2_only(z2_pts, cand_form2c, cand_form2d, cand_form2a, cand_form2b);

    MB = max_bright; 
    base_d = z2e - cand_form2c; if(base_d < 1e-9) base_d = 1e-9;
    double term_z2e = safePowDelta(base_d, 0.33);
    base_d = z1e - cand_form2c; if(base_d < 1e-9) base_d = 1e-9;
    double term_z1e = safePowDelta(base_d, 0.33);

    boundary_y2_end = cand_form2a + cand_form2b * (term_z2e - term_z1e);
    cand_form3a = 0.0; 
    if (MB > 0.01) cand_form3a = z2e * (MB - boundary_y2_end) / MB;
    if (cand_form3a < 0.0) cand_form3a = 0.0;

    z1_nrmse = 0.0; z1_bias = 0.0; z2_nrmse = 0.0; z2_bias = 0.0; z3_nrmse = 0.0; z3_bias = 0.0; cand_r2_z3 = -2.0;

    if (z1_pts.size() > 0) {
        sum_sq = 0.0; sum_err = 0.0; sum_w = 0.0;
        for (j = 0; j < z1_pts.size(); j++) {
            pt1 = (double[]) z1_pts.get(j); w = getLogWeight(pt1);
            y_pred1 = cand_form1a * Math.sqrt(pt1[0]); e1 = y_pred1 - pt1[1];
            sum_err += w * e1; sum_sq += w * e1 * e1; sum_w += w;
        } 
        if (sum_w > 1e-9) { z1_bias = sum_err / sum_w; z1_nrmse = Math.sqrt(sum_sq / sum_w) / max_bright; }
    }

    if (z2_pts.size() > 0) {
        sum_sq2 = 0.0; sum_err2 = 0.0; sum_w2 = 0.0; valid_z2_pts = 0;
        for (j = 0; j < z2_pts.size(); j++) {
            pt2m = (double[]) z2_pts.get(j); x2 = pt2m[0]; if (x2 <= cand_form2c) continue;
            w = getLogWeight(pt2m);
            term_x2 = safePowDelta(x2 - cand_form2c, 0.33); term_d2 = safePowDelta(z1e - cand_form2c, 0.33);
            y_pred2 = cand_form2a + cand_form2b * (term_x2 - term_d2);
            e2 = y_pred2 - pt2m[1]; sum_err2 += w * e2; sum_sq2 += w * e2 * e2; sum_w2 += w; valid_z2_pts++;
        } 
        if (valid_z2_pts > 0 && sum_w2 > 1e-9) {
            z2_bias = sum_err2 / sum_w2; z2_nrmse = Math.sqrt(sum_sq2 / sum_w2) / max_bright;
        }
    }

    if (z3_pts.size() > 0) {
        sum_wy_z3 = 0.0; sum_w_z3 = 0.0;
        for(j=0; j<z3_pts.size(); j++) { 
            pt3m = (double[])z3_pts.get(j); w = getLogWeight(pt3m);
            sum_wy_z3 += w * pt3m[1]; sum_w_z3 += w;
        }
        mean_y_z3 = (sum_w_z3 > 1e-9) ? (sum_wy_z3 / sum_w_z3) : 0.0;

        sum_sq3 = 0.0; sum_err3 = 0.0; ss_total_z3 = 0.0; ss_res_z3 = 0.0;
        for (j = 0; j < z3_pts.size(); j++) {
            pt3m = (double[]) z3_pts.get(j); w = getLogWeight(pt3m);
            y_pred3m = MB - (cand_form3a / pt3m[0]) * MB;
            e3 = y_pred3m - pt3m[1];
            sum_err3 += w * e3; sum_sq3 += w * e3 * e3;
            ss_total_z3 += w * Math.pow(pt3m[1] - mean_y_z3, 2.0);
            ss_res_z3 += w * e3 * e3;
        }
        if (sum_w_z3 > 1e-9) { z3_bias = sum_err3 / sum_w_z3; z3_nrmse = Math.sqrt(sum_sq3 / sum_w_z3) / max_bright; }
        if (ss_total_z3 > 1e-9) cand_r2_z3 = 1.0 - (ss_res_z3 / ss_total_z3); else cand_r2_z3 = 0.0;
    }

    size_penalty = calculateSizePenalty(z1_pts.size()) + calculateSizePenalty(z2_pts.size()) + calculateSizePenalty(z3_pts.size());
    reg_penalty = 0.001 * cand_form2b * cand_form2b + 0.0005 * Math.abs(cand_form2c);

    double z3_weight = 1.0;
    r2_penalty = 0.0;
    if (cand_r2_z3 > -2.0) {
        if (cand_r2_z3 < 0.65) { z3_weight = 0.5; r2_penalty += 0.5 * (0.65 - cand_r2_z3); }
    } else { if (N > 12) size_penalty += 0.5; }

    cost = z1_nrmse + z2_nrmse + z3_weight*z3_nrmse + (Math.abs(z1_bias) + Math.abs(z2_bias) + Math.abs(z3_bias)) + size_penalty + reg_penalty + r2_penalty;

    results[0] = cost; results[1] = z1e; results[2] = z2e; results[3] = cand_form1a;
    results[4] = cand_form2a; results[5] = cand_form2b; results[6] = cand_form2c;
    results[7] = cand_form2d; results[8] = cand_form3a; results[9] = cand_r2_z1;
    results[10] = cand_r2_z2; results[11] = cand_r2_z3; results[12] = z1_nrmse;
    results[13] = z2_nrmse; results[14] = z3_nrmse; results[15] = z1_bias;
    results[16] = z2_bias; results[17] = z3_bias;

    return results;
}


/* ======================= MAIN ======================= */
try {
    /* Declare variables explicitly to prevent Rhino/BeanShell errors */
    int arraySize = 0, N, TOP_K_Z1, i, k, t, R, j, pt_idx, i_z1, i_z2;
    double current_form1a, current_form2a, current_form2b, current_form2c, current_zone1end, current_zone2end, max_bright, current_lux;
    double[] firstPt, lastPt, z1_scores, z1_values, fitZ1, fitZ3;
    double tmpS, tmpV, combinedScore, r2_z1_temp, r2_z2_temp, r2_z3_temp, temp_form2d;
    double global_bestCost = 1e12, best_z1_end, best_z2_end, best_form1a, best_form2a, best_form2b;
    double best_form2c, best_form2d, best_form3a, best_r2_z1, best_r2_z2, best_r2_z3;
    double best_z1_nrmse, best_z2_nrmse, best_z3_nrmse, best_z1_bias, best_z2_bias, best_z3_bias;
    double cand_z1_end, best_z2_score_local, cand_z2_end, combined_z2z3;
    double lux_before_z1, lux_after_z1, lux_before_z2, lux_after_z2, refined_z1_end, refined_z2_end;
    double k_bestCost, EARLY_STOP_COST;
    double[] pt, initial_results, z1_refine_results, z2_refine_results, k_best_results;
    List dataPoints, remainingPoints, temp_zone1, temp_zone2, temp_zone3, z1_candidates, z2_candidates;
    List promising_z1_candidates, promising_z2_candidates, z1_approx_costs, z2_approx_costs;
    double best_approx_cost_z1, best_approx_z1e, approx_cost, best_approx_cost_z2, best_approx_z2e;
    int hop_count, MAX_HOPS; boolean hopped;
    double hop_threshold;
    String[] parts;
    String z1_r2_str, z2_r2_str, z3_r2_str, z1ErrQual, z2ErrQual, z3ErrQual, z1BiasDir, z2BiasDir, z3BiasDir;
    String z1BiasQual, z2BiasQual, z3BiasQual, z1ShapeQual, z2ShapeQual, z3ShapeQual, overallQual;
    String overallLine, z1Line, z2Line, z3Line;
    double z1_err_pct, z2_err_pct, z3_err_pct, avgErr, absB1, absB2, absB3;
    int iter_pass; 

    logBuffer.append("--- Engine V40.3 (Robustness Pack) ---\n");
    current_form1a = Double.parseDouble(tasker.getVariable("AAB_Form1A"));
    current_form2a = Double.parseDouble(tasker.getVariable("AAB_Form2A"));
    current_form2b = Double.parseDouble(tasker.getVariable("AAB_Form2B"));
    current_form2c = Double.parseDouble(tasker.getVariable("AAB_Form2C"));
    if (current_form2c < -50.0) current_form2c = -50.0;
    current_zone1end = Double.parseDouble(tasker.getVariable("AAB_Zone1End"));
    current_zone2end = Double.parseDouble(tasker.getVariable("AAB_Zone2End"));
    max_bright       = Double.parseDouble(tasker.getVariable("AAB_MaxBright"));

    logBuffer.append("[Input Parameters]\n");
    logBuffer.append(String.format("  Form1a (current): %.3f\n", new Object[]{new Double(current_form1a)}));
    logBuffer.append(String.format("  Form2a (current): %.3f\n", new Object[]{new Double(current_form2a)}));
    logBuffer.append(String.format("  Form2b (current): %.3f\n", new Object[]{new Double(current_form2b)}));
    logBuffer.append(String.format("  Form2c (current): %.3f\n", new Object[]{new Double(current_form2c)}));
    logBuffer.append(String.format("  Zone1End (current): %.1f\n", new Object[]{new Double(current_zone1end)}));
    logBuffer.append(String.format("  Zone2End (current): %.1f\n", new Object[]{new Double(current_zone2end)}));
    logBuffer.append(String.format("  MaxBright: %.1f\n\n", new Object[]{new Double(max_bright)}));

    /* Improved Parsing Logic (Iterative) */
    dataPoints = new ArrayList();
    int idx = 1;
    int emptyCount = 0; 

    while (idx < 1000) { 
        String val = tasker.getVariable("AAB_Overrides" + idx);

        if (val == null) {
            if (emptyCount > 2) break; 
            emptyCount++;
            idx++;
            continue;
        }
        emptyCount = 0; 

        try {
            parts = val.split(",");
            if (parts.length >= 2) {
                double[] newPt = new double[4];
                String p0 = parts[0].trim();
                String p1 = parts[1].trim();

                if (p0.length() > 0 && p1.length() > 0) {
                    newPt[0] = Double.parseDouble(p0); // Lux
                    newPt[1] = Double.parseDouble(p1); // Brightness

                    if (parts.length == 2) {
                        newPt[2] = 1.0; newPt[3] = 1.0; 
                    } else if (parts.length == 3) {
                        String p2 = parts[2].trim();
                        newPt[2] = (p2.length() > 0) ? Double.parseDouble(p2) : 1.0;
                        newPt[3] = 2.0; 
                    } else {
                        String p2 = parts[2].trim();
                        String p3 = parts[3].trim();
                        newPt[2] = (p2.length() > 0) ? Double.parseDouble(p2) : 1.0;
                        newPt[3] = (p3.length() > 0) ? Double.parseDouble(p3) : 1.0;
                    }
                    dataPoints.add(newPt);
                }
            }
        } catch (Exception parseEx) {}
        idx++;
    }

    Collections.sort(dataPoints, new Comparator() {
        public int compare(Object o1, Object o2) {
            double[] p1 = (double[]) o1; 
            double[] p2 = (double[]) o2;
            return Double.compare(p1[0], p2[0]);
        }
    });

    /* --- GHOST POINT INJECTION (Logic Bins) --- */
    int realCount = 0;
    boolean[] binsFilled = new boolean[]{false, false, false, false, false};

    for (i=0; i<dataPoints.size(); i++) {
        pt = (double[]) dataPoints.get(i);
        if (pt.length > 3 && pt[3] == 1.0) {
            realCount++;
            double lx = pt[0];
            if (lx < 10.0) binsFilled[0] = true;
            else if (lx < 100.0) binsFilled[1] = true;
            else if (lx < 1000.0) binsFilled[2] = true;
            else if (lx < 10000.0) binsFilled[3] = true;
            else binsFilled[4] = true;
        }
    }

    double ghostWeight = 0.1 + (0.4 * (realCount / 50.0));
    if (ghostWeight > 0.5) ghostWeight = 0.5;

    double cur_term_d = Math.pow(current_zone1end - current_form2c, 0.33);
    double cur_y_z2 = current_form2a + current_form2b * (Math.pow(current_zone2end - current_form2c, 0.33) - cur_term_d);
    double cur_form3a = 0.0;
    if (max_bright > 0.01) cur_form3a = current_zone2end * (max_bright - cur_y_z2) / max_bright;
    if (cur_form3a < 0.0) cur_form3a = 0.0;

    double[] ghostLuxes = {3.0, 31.0, 316.0, 3162.0, 15000.0};
    boolean ghostsAdded = false;

    for (i=0; i<5; i++) {
        if (!binsFilled[i]) {
            double gLux = ghostLuxes[i];
            double gBright = 0.0;

            if (gLux <= current_zone1end) {
                String f1aStr = tasker.getVariable("AAB_Form1A");
                double f1a = 0.0;
                if (f1aStr != null && f1aStr.length() > 0) {
                     try { f1a = Double.parseDouble(f1aStr); } catch(Exception e){}
                }
                if (f1a <= 1e-9 && dataPoints.size() > 0) {
                     double[] fpt = (double[]) dataPoints.get(0);
                     if (fpt[0] > 1e-9) f1a = fpt[1] / Math.sqrt(fpt[0]);
                }
                gBright = f1a * Math.sqrt(gLux);
            } else if (gLux <= current_zone2end) {
                gBright = current_form2a + current_form2b * (Math.pow(gLux - current_form2c, 0.33) - cur_term_d);
            } else {
                gBright = max_bright - (cur_form3a / gLux) * max_bright;
            }
            if (gBright < 0.0) gBright = 0.0;
            if (gBright > max_bright) gBright = max_bright;
            dataPoints.add(new double[]{gLux, gBright, ghostWeight, 3.0});
            ghostsAdded = true;
            logBuffer.append("  + Ghost Point: " + gLux + " lux -> " + Math.round(gBright) + " br (Bin " + (i+1) + " empty)\n");
        }
    }

    if (ghostsAdded) {
        Collections.sort(dataPoints, new Comparator() {
            public int compare(Object o1, Object o2) {
                double[] p1 = (double[]) o1; double[] p2 = (double[]) o2; return Double.compare(p1[0], p2[0]);
            }
        });
    }

    /* FIX 2: Global Weight Normalization */
    double sumRaw = 0.0; int cntRaw = 0;
    for (i=0; i<dataPoints.size(); i++) {
        pt = (double[]) dataPoints.get(i);
        // Check for REAL points (Real=1.0)
        boolean isReal = true;
        if (pt.length > 3 && Math.abs(pt[3] - 1.0) > 0.1) isReal = false;

        if (isReal) { sumRaw += pt[2]; cntRaw++; }
    }
    double meanRaw = (cntRaw > 0) ? (sumRaw / cntRaw) : 1.0;
    double globalScale = 1.0 / Math.max(meanRaw, 1e-9);

    for (i=0; i<dataPoints.size(); i++) {
        pt = (double[]) dataPoints.get(i);
        pt[2] = pt[2] * globalScale;
    }
    logBuffer.append(String.format("Global Weight Norm: Scale=%.4f (Mean Raw=%.4f)\n\n", new Object[]{new Double(globalScale), new Double(meanRaw)}));

    if (dataPoints.size() < 9) {
        logBuffer.append("Not enough data points (" + dataPoints.size() + "). Aborting.\n");
        tasker.setVariable("AAB_Test", logBuffer.toString());
        return;
    }

    firstPt = (double[]) dataPoints.get(0);
    lastPt = (double[]) dataPoints.get(dataPoints.size() - 1);
    logBuffer.append("[Input Data] " + dataPoints.size() + " points from " + firstPt[0] + " to " + lastPt[0] + " lux.\n\n");

    N = dataPoints.size();
    TOP_K_Z1 = Math.min(5, N - 8);
    if (TOP_K_Z1 < 1) TOP_K_Z1 = 1;
    EARLY_STOP_COST = 0.5;
    MAX_HOPS = Math.min(8, Math.max(3, (int)(N / 5)));

    logBuffer.append("--- Stage 1: Searching for Top " + TOP_K_Z1 + " Zone1End Candidates ---\n");
    z1_scores = new double[TOP_K_Z1]; z1_values = new double[TOP_K_Z1];
    for (i = 0; i < TOP_K_Z1; i++) { z1_scores[i] = -9999.0; z1_values[i] = current_zone1end; }

    for (i = 2; i < N - 6; i++) {
        temp_zone1 = dataPoints.subList(0, i + 1); temp_zone2 = dataPoints.subList(i + 1, N);
        if (temp_zone1.size() < 3 || temp_zone2.size() < 3) continue;
        fitZ1 = fitZone1(temp_zone1); r2_z1_temp = fitZ1[1];
        temp_form2d = ((double[]) temp_zone1.get(temp_zone1.size() - 1))[0];

        r2_z2_temp = getR2_Z2_only(temp_zone2, current_form2c, temp_form2d, current_form2a, current_form2b);
        combinedScore = r2_z1_temp + r2_z2_temp;
        if (combinedScore > z1_scores[TOP_K_Z1 - 1]) {
            z1_scores[TOP_K_Z1 - 1] = combinedScore; z1_values[TOP_K_Z1 - 1] = temp_form2d;
            for (t = TOP_K_Z1 - 1; t > 0; t--) {
                if (z1_scores[t] > z1_scores[t - 1]) {
                    tmpS = z1_scores[t - 1]; tmpV = z1_values[t - 1];
                    z1_scores[t - 1] = z1_scores[t]; z1_values[t - 1] = z1_values[t];
                    z1_scores[t] = tmpS; z1_values[t] = tmpV;
                }
            }
        }
    }
    for (i = 0; i < TOP_K_Z1; i++) {
        logBuffer.append(String.format("  Top Cand #%d: Z1End=%.2f (Score: %.3f)\n", new Object[]{new Integer(i+1), new Double(z1_values[i]), new Double(z1_scores[i])}));
    }

    logBuffer.append("\n--- Stage 2 & 3: Iterative Coordinate Descent ---\n");
    global_bestCost = 1e12;

    for (k = 0; k < TOP_K_Z1; k++) {
        if (k > 0 && global_bestCost < EARLY_STOP_COST) {
            logBuffer.append(" -> Cost already excellent, terminating search early.\n");
            break;
        }
        if (z1_scores[k] <= -9998.0) continue;

        cand_z1_end = z1_values[k];
        remainingPoints = new ArrayList(N);
        for (i = 0; i < N; i++) {
            pt = (double[]) dataPoints.get(i); if (pt[0] >= cand_z1_end) remainingPoints.add(pt);
        }
        if (remainingPoints.size() < 6) continue;
        best_z2_score_local = -9999.0; cand_z2_end = current_zone2end;
        R = remainingPoints.size();
        for (j = 2; j < R - 3; j++) {
            temp_zone2 = remainingPoints.subList(0, j + 1); temp_zone3 = remainingPoints.subList(j + 1, R);
            if (temp_zone2.size() < 3 || temp_zone3.size() < 3) continue;

            r2_z2_temp = getR2_Z2_only(temp_zone2, current_form2c, cand_z1_end, current_form2a, current_form2b);
            fitZ3 = fitZone3(temp_zone3, max_bright);
            r2_z3_temp = fitZ3[1]; combined_z2z3 = r2_z2_temp + r2_z3_temp;
            if (combined_z2z3 > best_z2_score_local) {
                best_z2_score_local = combined_z2z3;
                cand_z2_end = ((double[]) temp_zone2.get(temp_zone2.size() - 1))[0];
            }
        }
        if (cand_z2_end < 10.0) cand_z2_end = 10.0;
        logBuffer.append(String.format(" -> Refining Cand(k=%d): Initial Z1e=%.2f, Z2e=%.1f\n", new Object[]{new Integer(k), new Double(cand_z1_end), new Double(cand_z2_end)}));

        k_best_results = calculateFitAndCost(cand_z1_end, cand_z2_end, dataPoints, N, current_form2b, current_form2c, max_bright);
        /* If initialization fails, skip */
        if (k_best_results[0] > 1e11) {
             logBuffer.append("    ! Initialization failed (invalid bounds). Skipping.\n");
             continue;
        }
        k_bestCost = k_best_results[0];

        /* Iterative Refinement Loop (Coordinate Descent) */
        for (iter_pass = 0; iter_pass < 2; iter_pass++) {

            /* Refine Z1 */
            hop_count = 0; hopped = true;
            while (hopped && hop_count < MAX_HOPS && k_best_results[12] > 0.015) {
                hopped = false;
                lux_before_z1 = ((double[]) dataPoints.get(0))[0]; lux_after_z1 = ((double[]) dataPoints.get(dataPoints.size() - 1))[0];
                for(pt_idx = 1; pt_idx < dataPoints.size(); pt_idx++) {
                    current_lux = ((double[]) dataPoints.get(pt_idx))[0];
                    if (current_lux >= k_best_results[1]) {
                        lux_before_z1 = ((double[]) dataPoints.get(pt_idx - 1))[0];
                        if (pt_idx < dataPoints.size() - 1) lux_after_z1 = ((double[]) dataPoints.get(pt_idx + 1))[0]; else lux_after_z1 = current_lux;
                        break;
                    }
                }
                z1_candidates = generateSmartRefinementPoints(lux_before_z1, lux_after_z1, k_best_results[1], calculateRefinePoints(lux_before_z1, lux_after_z1, k_best_results[1]));

                best_approx_cost_z1 = 1e12; best_approx_z1e = k_best_results[1];
                for (i_z1 = 0; i_z1 < z1_candidates.size(); i_z1++) {
                    refined_z1_end = ((Double)z1_candidates.get(i_z1)).doubleValue();
                    /* Safe proximity check */
                    if (refined_z1_end >= k_best_results[2] - 1.0) continue;

                    approx_cost = approximateCost(refined_z1_end, k_best_results[2], dataPoints, N, k_best_results[3], k_best_results[5], k_best_results[6], max_bright);
                    if (approx_cost < best_approx_cost_z1) { best_approx_cost_z1 = approx_cost; best_approx_z1e = refined_z1_end; }
                }

                hop_threshold = Math.max(0.5, k_best_results[1] * 0.005);
                if (Math.abs(best_approx_z1e - k_best_results[1]) > hop_threshold) {
                    z1_refine_results = calculateFitAndCost(best_approx_z1e, k_best_results[2], dataPoints, N, k_best_results[5], k_best_results[6], max_bright);
                    if (z1_refine_results[0] < k_bestCost) {
                        k_bestCost = z1_refine_results[0]; k_best_results = z1_refine_results;
                        hopped = true; hop_count++;
                        logBuffer.append(String.format("    [Pass %d] * Z1 Hopped to %.2f (Cost: %.4f)\n", new Object[]{new Integer(iter_pass+1), new Double(k_best_results[1]), new Double(k_bestCost)}));
                    }
                }
            }

            /* Refine Z2 */
            if (k_bestCost >= EARLY_STOP_COST) {
                 remainingPoints = new ArrayList(N);
                 for (i = 0; i < N; i++) { pt = (double[]) dataPoints.get(i); if (pt[0] >= k_best_results[1]) remainingPoints.add(pt); }
                 if (remainingPoints.size() >= 6) {
                    hop_count = 0; hopped = true;
                    while (hopped && hop_count < MAX_HOPS) {
                        hopped = false;
                        lux_before_z2 = ((double[]) remainingPoints.get(0))[0]; lux_after_z2 = ((double[]) remainingPoints.get(remainingPoints.size() - 1))[0];
                        for(pt_idx = 1; pt_idx < remainingPoints.size(); pt_idx++) {
                            current_lux = ((double[]) remainingPoints.get(pt_idx))[0];
                            if (current_lux >= k_best_results[2]) {
                                lux_before_z2 = ((double[]) remainingPoints.get(pt_idx - 1))[0];
                                if (pt_idx < remainingPoints.size() - 1) lux_after_z2 = ((double[]) remainingPoints.get(pt_idx + 1))[0]; else lux_after_z2 = current_lux;
                                break;
                            }
                        }
                        z2_candidates = generateSmartRefinementPoints(lux_before_z2, lux_after_z2, k_best_results[2], calculateRefinePoints(lux_before_z2, lux_after_z2, k_best_results[2]));

                        best_approx_cost_z2 = 1e12; best_approx_z2e = k_best_results[2];
                        for (i_z2 = 0; i_z2 < z2_candidates.size(); i_z2++) {
                            refined_z2_end = ((Double)z2_candidates.get(i_z2)).doubleValue();
                            /* Safe proximity check */
                            if (refined_z2_end <= k_best_results[1] + 1.0) continue;

                            approx_cost = approximateCost(k_best_results[1], refined_z2_end, dataPoints, N, k_best_results[3], k_best_results[5], k_best_results[6], max_bright);
                            if (approx_cost < best_approx_cost_z2) { best_approx_cost_z2 = approx_cost; best_approx_z2e = refined_z2_end; }
                        }

                        hop_threshold = Math.max(0.5, k_best_results[2] * 0.005);
                        if (Math.abs(best_approx_z2e - k_best_results[2]) > hop_threshold) {
                            z2_refine_results = calculateFitAndCost(k_best_results[1], best_approx_z2e, dataPoints, N, k_best_results[5], k_best_results[6], max_bright);
                            if(z2_refine_results[0] < k_bestCost) { 
                                k_bestCost = z2_refine_results[0]; k_best_results = z2_refine_results; 
                                hopped = true; hop_count++;
                                logBuffer.append(String.format("    [Pass %d] * Z2 Hopped to %.2f (Cost: %.4f)\n", new Object[]{new Integer(iter_pass+1), new Double(k_best_results[2]), new Double(k_bestCost)}));
                            }
                        }
                    }
                 }
            }
        } 

        logBuffer.append(String.format("    Refined Best: Z1e=%.2f, Z2e=%.1f, Cost=%.4f\n", new Object[]{new Double(k_best_results[1]), new Double(k_best_results[2]), new Double(k_best_results[0])}));

        if (k_best_results[0] < global_bestCost) {
            global_bestCost = k_best_results[0];
            best_z1_end = k_best_results[1]; best_z2_end = k_best_results[2];
            best_form1a = k_best_results[3]; best_form2a = k_best_results[4]; best_form2b = k_best_results[5];
            best_form2c = k_best_results[6]; best_form2d = k_best_results[7]; best_form3a = k_best_results[8];
            best_r2_z1 = k_best_results[9]; best_r2_z2 = k_best_results[10]; best_r2_z3 = k_best_results[11];
            best_z1_nrmse = k_best_results[12]; best_z2_nrmse = k_best_results[13]; best_z3_nrmse = k_best_results[14];
            best_z1_bias = k_best_results[15]; best_z2_bias = k_best_results[16]; best_z3_bias = k_best_results[17];
            suggestion_made = true;
        }
    }

    if (!suggestion_made) {
        logBuffer.append("\n--- No valid candidate found. ---\n");
        logBuffer.append("⚠️ FALLBACK TO CURRENT PARAMETERS\n");
        tasker.setVariable("suggest", "fallback");
    } else {
        /* FIX 3: Inertia / Regularization Blending */
        int nz1 = 0, nz2 = 0, nz3 = 0;
        for(i=0; i<dataPoints.size(); i++) {
            pt = (double[]) dataPoints.get(i);
            boolean isReal = (pt.length <= 3 || Math.abs(pt[3] - 1.0) < 0.1);
            if (!isReal) continue;
            if (pt[0] <= best_z1_end) nz1++;
            else if (pt[0] <= best_z2_end) nz2++;
            else nz3++;
        }

        /* Logic update: Use quality (R2, nRMSE) AND quantity (N) to determine blend strength */
        double n1_w = getWeightedCount(nz1, best_r2_z1, best_z1_nrmse);
        double n2_w = getWeightedCount(nz2, best_r2_z2, best_z2_nrmse);
        double n3_w = getWeightedCount(nz3, best_r2_z3, best_z3_nrmse);

        /* TASK: Read tau from variable, default to 4.0 */
        double tau = 4.0;
        String tVal = tasker.getVariable("tau");
        if (tVal != null && tVal.length() > 0) {
            try { 
                tau = Double.parseDouble(tVal); 
            } catch (Exception e) { 
                tau = 4.0; 
            }
        }
        if (Double.isNaN(tau)) tau = 4.0;

        double conf_z1 = 1.0 - Math.exp(-n1_w / tau);
        double conf_z2 = 1.0 - Math.exp(-n2_w / tau);
        double conf_z3 = 1.0 - Math.exp(-n3_w / tau);

        logBuffer.append(String.format("\n[Inertia Blending] Confidence: Z1=%.2f, Z2=%.2f, Z3=%.2f (Tau=%.1f)\n", new Object[]{new Double(conf_z1), new Double(conf_z2), new Double(conf_z3), new Double(tau)}));

        // Blend Z1
        best_z1_end = best_z1_end * conf_z1 + current_zone1end * (1.0 - conf_z1);
        best_form1a = best_form1a * conf_z1 + current_form1a * (1.0 - conf_z1);

        // Blend Z2
        best_z2_end = best_z2_end * conf_z2 + current_zone2end * (1.0 - conf_z2);
        best_form2a = best_form2a * conf_z2 + current_form2a * (1.0 - conf_z2);
        best_form2b = best_form2b * conf_z2 + current_form2b * (1.0 - conf_z2);
        best_form2c = best_form2c * conf_z2 + current_form2c * (1.0 - conf_z2);

        // Form2D is tied to Z1 end
        best_form2d = best_z1_end; 

        // Re-calculate Dependent Continuity Params (Form3A) based on blended values
        double term_d_final = safePowDelta(best_z1_end - best_form2c, 0.33);
        double term_x_final = safePowDelta(best_z2_end - best_form2c, 0.33);
        double y_boundary_z2 = best_form2a + best_form2b * (term_x_final - term_d_final);

        if (max_bright > 0.01) best_form3a = best_z2_end * (max_bright - y_boundary_z2) / max_bright;
        else best_form3a = 0.0;
        if (best_form3a < 0.0) best_form3a = 0.0;

        tasker.setVariable("suggest", "true");
        tasker.setVariable("suggestion_zone1end", String.valueOf(Math.round(best_z1_end)));
        tasker.setVariable("suggestion_zone2end", String.valueOf(Math.round(best_z2_end)));
        tasker.setVariable("suggestion_form1a", String.format("%.3f", new Object[]{new Double(best_form1a)}));
        tasker.setVariable("suggestion_form2a", String.format("%.3f", new Object[]{new Double(best_form2a)}));
        tasker.setVariable("suggestion_form2b", String.format("%.3f", new Object[]{new Double(best_form2b)}));
        tasker.setVariable("suggestion_form2c", String.format("%.3f", new Object[]{new Double(best_form2c)}));
        tasker.setVariable("suggestion_form2d", String.valueOf(Math.round(best_form2d)));
        tasker.setVariable("suggestion_form3a", String.format("%.3f", new Object[]{new Double(best_form3a)}));

        double max_impact = 0.0; double w;
        if (dataPoints.size() > 3) {
            double base_sq_err = 0.0; double y_curve;
            double term_d_z2 = safePowDelta(best_z1_end - best_form2c, 0.33);
            double mb_z3_term = (best_z2_end * (max_bright - (best_form2a + best_form2b*(safePowDelta(best_z2_end-best_form2c,0.33)-term_d_z2)))/max_bright);
            if(mb_z3_term < 0) mb_z3_term = 0.0;

            for (i=0; i<dataPoints.size(); i++) {
                pt = (double[])dataPoints.get(i); w = getLogWeight(pt);
                if(pt[0] <= best_z1_end) y_curve = best_form1a * Math.sqrt(pt[0]);
                else if(pt[0] <= best_z2_end) y_curve = best_form2a + best_form2b * (safePowDelta(pt[0]-best_form2c, 0.33) - term_d_z2);
                else y_curve = max_bright - (mb_z3_term / pt[0]) * max_bright;
                base_sq_err += w * Math.pow(y_curve - pt[1], 2.0);
            }

            for (i=0; i<dataPoints.size(); i++) {
                pt = (double[])dataPoints.get(i); w = getLogWeight(pt);
                if(pt[0] <= best_z1_end) y_curve = best_form1a * Math.sqrt(pt[0]);
                else if(pt[0] <= best_z2_end) y_curve = best_form2a + best_form2b * (safePowDelta(pt[0]-best_form2c, 0.33) - term_d_z2);
                else y_curve = max_bright - (mb_z3_term / pt[0]) * max_bright;
                double err_contrib = w * Math.pow(y_curve - pt[1], 2.0);
                double impact = err_contrib / (base_sq_err + 1e-9);
                if(impact > max_impact) max_impact = impact;
            }
        }
        String stabilityRating = (max_impact < 0.3) ? "High" : (max_impact < 0.6) ? "Moderate" : "Low (Outlier Driven)";

        logBuffer.append("\n--- Best Fit Diagnostics ---\n");
        logBuffer.append(String.format("Cost Function Value: %.5f\n", new Object[]{new Double(global_bestCost)}));
        logBuffer.append("Fit Stability: " + stabilityRating + String.format(" (Max Impact: %.1f%%)\n\n", new Object[]{new Double(max_impact*100.0)}));
        logBuffer.append("[Zone Boundaries]\n");
        logBuffer.append(String.format("  Zone1End: %.2f (lux)\n", new Object[]{new Double(best_z1_end)}));
        logBuffer.append(String.format("  Zone2End: %.2f (lux)\n\n", new Object[]{new Double(best_z2_end)}));
        logBuffer.append("[Curve Parameters]\n");
        logBuffer.append(String.format("  Form1a (sqrt scale): %.4f\n", new Object[]{new Double(best_form1a)}));
        logBuffer.append(String.format("  Form2a (align): %.4f\n", new Object[]{new Double(best_form2a)}));
        logBuffer.append(String.format("  Form2b (scale): %.4f\n", new Object[]{new Double(best_form2b)}));
        logBuffer.append(String.format("  Form2c (offset): %.4f\n", new Object[]{new Double(best_form2c)}));
        logBuffer.append(String.format("  Form2d (Z1 align): %.4f\n", new Object[]{new Double(best_form2d)}));
        logBuffer.append(String.format("  Form3a (tail align): %.4f\n\n", new Object[]{new Double(best_form3a)}));
        logBuffer.append("[Goodness of Fit (R²)]\n");
        logBuffer.append(String.format("  R² Zone 1: %.3f\n", new Object[]{new Double(best_r2_z1)}));
        logBuffer.append(String.format("  R² Zone 2: %.3f\n", new Object[]{new Double(best_r2_z2)}));
        logBuffer.append(String.format("  R² Zone 3: %.3f\n\n", new Object[]{new Double(best_r2_z3)}));
        logBuffer.append("[Error Metrics]\n");
        logBuffer.append(String.format("  nRMSE Z1: %.2f%%\n", new Object[]{new Double(best_z1_nrmse * 100.0)}));
        logBuffer.append(String.format("  nRMSE Z2: %.2f%%\n", new Object[]{new Double(best_z2_nrmse * 100.0)}));
        logBuffer.append(String.format("  nRMSE Z3: %.2f%%\n", new Object[]{new Double(best_z3_nrmse * 100.0)}));
        logBuffer.append(String.format("  Bias Z1:  %.2f\n", new Object[]{new Double(best_z1_bias)}));
        logBuffer.append(String.format("  Bias Z2:  %.2f\n", new Object[]{new Double(best_z2_bias)}));
        logBuffer.append(String.format("  Bias Z3:  %.2f\n", new Object[]{new Double(best_z3_bias)}));
        logBuffer.append("--------------------------\n\n");

        z1_r2_str = (best_r2_z1 > -2.0) ? String.format("%.2f", new Object[]{new Double(best_r2_z1)}) : "N/A";
        z2_r2_str = (best_r2_z2 > -2.0) ? String.format("%.2f", new Object[]{new Double(best_r2_z2)}) : "N/A";
        z3_r2_str = (best_r2_z3 > -2.0) ? String.format("%.2f", new Object[]{new Double(best_r2_z3)}) : "N/A";

        z1_err_pct = best_z1_nrmse * 100.0; z2_err_pct = best_z2_nrmse * 100.0; z3_err_pct = best_z3_nrmse * 100.0;
        if (z1_err_pct < 1) z1ErrQual = "Excellent"; else if (z1_err_pct < 3) z1ErrQual = "Very Good"; else if (z1_err_pct < 7) z1ErrQual = "Good"; else if (z1_err_pct < 15) z1ErrQual = "Fair"; else z1ErrQual = "Poor";
        if (z2_err_pct < 1) z2ErrQual = "Excellent"; else if (z2_err_pct < 3) z2ErrQual = "Very Good"; else if (z2_err_pct < 7) z2ErrQual = "Good"; else if (z2_err_pct < 15) z2ErrQual = "Fair"; else z2ErrQual = "Poor";
        if (z3_err_pct < 1) z3ErrQual = "Excellent"; else if (z3_err_pct < 3) z3ErrQual = "Very Good"; else if (z3_err_pct < 7) z3ErrQual = "Good"; else if (z3_err_pct < 15) z3ErrQual = "Fair"; else z3ErrQual = "Poor";
        if (best_z1_bias > 0.2) z1BiasDir = "brighter"; else if (best_z1_bias < -0.2) z1BiasDir = "dimmer"; else z1BiasDir = "neutral";
        if (best_z2_bias > 0.2) z2BiasDir = "brighter"; else if (best_z2_bias < -0.2) z2BiasDir = "dimmer"; else z2BiasDir = "neutral";
        if (best_z3_bias > 0.2) z3BiasDir = "brighter"; else if (best_z3_bias < -0.2) z3BiasDir = "dimmer"; else z3BiasDir = "neutral";
        absB1 = Math.abs(best_z1_bias); absB2 = Math.abs(best_z2_bias); absB3 = Math.abs(best_z3_bias);
        if (absB1 < 0.5) z1BiasQual = "Minimal"; else if (absB1 < 2) z1BiasQual = "Slight"; else if (absB1 < 5) z1BiasQual = "Moderate"; else z1BiasQual = "Strong";
        if (absB2 < 0.5) z2BiasQual = "Minimal"; else if (absB2 < 2) z2BiasQual = "Slight"; else if (absB2 < 5) z2BiasQual = "Moderate"; else z2BiasQual = "Strong";
        if (absB3 < 0.5) z3BiasQual = "Minimal"; else if (absB3 < 2) z3BiasQual = "Slight"; else if (absB3 < 5) z3BiasQual = "Moderate"; else z3BiasQual = "Strong";
        if (best_r2_z1 >= 0.9) z1ShapeQual = "Excellent"; else if (best_r2_z1 >= 0.75) z1ShapeQual = "Good"; else if (best_r2_z1 >= 0.4) z1ShapeQual = "Moderate"; else z1ShapeQual = "Poor";
        if (best_r2_z2 >= 0.9) z2ShapeQual = "Excellent"; else if (best_r2_z2 >= 0.75) z2ShapeQual = "Good"; else if (best_r2_z2 >= 0.4) z2ShapeQual = "Moderate"; else z2ShapeQual = "Poor";
        if (best_r2_z3 >= 0.9) z3ShapeQual = "Excellent"; else if (best_r2_z3 >= 0.75) z3ShapeQual = "Good"; else if (best_r2_z3 >= 0.4) z3ShapeQual = "Moderate"; else z3ShapeQual = "Poor";
        avgErr = (z1_err_pct + z2_err_pct + z3_err_pct) / 3.0;
        if (avgErr < 2) overallQual = "Excellent"; else if (avgErr < 4) overallQual = "Very Good"; else if (avgErr < 8) overallQual = "Good"; else if (avgErr < 15) overallQual = "Fair"; else overallQual = "Poor";

        overallLine = "🏆 Overall Fit: " + overallQual;
        z1Line = String.format("⚫ Dark: %s (%.1f%%) | Shape: %s (R²: %s) | Bias: %s %s (%.1f)", new Object[]{z1ErrQual, new Double(z1_err_pct), z1ShapeQual, z1_r2_str, z1BiasQual, z1BiasDir, new Double(best_z1_bias)});
        z2Line = String.format("⚪ Dim: %s (%.1f%%) | Shape: %s (R²: %s) | Bias: %s %s (%.1f)", new Object[]{z2ErrQual, new Double(z2_err_pct), z2ShapeQual, z2_r2_str, z2BiasQual, z2BiasDir, new Double(best_z2_bias)});
        z3Line = String.format("☀️ Bright: %s (%.1f%%) | Shape: %s (R²: %s) | Bias: %s %s (%.1f)", new Object[]{z3ErrQual, new Double(z3_err_pct), z3ShapeQual, z3_r2_str, z3BiasQual, z3BiasDir, new Double(best_z3_bias)});

        tasker.setVariable("suggest_r2_1", overallLine);
        tasker.setVariable("suggest_r2_2", z1Line);
        tasker.setVariable("suggest_r2_3", z2Line);
        tasker.setVariable("suggest_r2_4", z3Line);

        logBuffer.append("[Human Summary]\n" + overallLine + "\n" + z1Line + "\n" + z2Line + "\n" + z3Line + "\n");
    }
} catch (Exception e) {
    logBuffer.append("CRITICAL ERROR: " + e.toString() + "\n");
    e.printStackTrace();
} finally {
    logBuffer.append("\n--- Analysis Engine Finished ---\n");
    tasker.setVariable("AAB_Test", logBuffer.toString());
}

import android.content.Context;
import android.content.SharedPreferences;
import android.content.pm.PackageManager;
import android.app.Notification;
import android.app.NotificationManager;
import android.app.NotificationChannel;
import android.os.Build;
import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.net.Socket;
import java.net.InetSocketAddress;

/* Retrieve Tasker variables */
String caller = tasker.getVariable("caller1");
String privilege = tasker.getVariable("AAB_Privilege");
String notifyUse = tasker.getVariable("AAB_NotifyUse");

/* Helper booleans for logic conditions */
boolean isAnon = (caller != null && caller.matches(".*anon.*"));
boolean isSetInitialBrightness = (caller != null && caller.matches(".*Set Initial Brightness.*"));
boolean isPrivilegeSet = (privilege != null && privilege.length() > 0);

/* A1: Clear privilege if caller matches specific task */
if (isSetInitialBrightness) {
    tasker.setVariable("AAB_Privilege", null);
    privilege = null;
    isPrivilegeSet = false;
}

/* A4: Main Detection Block */
/* Logic: If Privilege is NOT Set AND Caller is NOT anon */
if (!isPrivilegeSet && !isAnon) {

    boolean hasPrivilegeFound = false;

    /* A5/A6: Check Root */
    try {
        Process p = Runtime.getRuntime().exec("su");
        /* Close output stream to prevent blocking */
        p.getOutputStream().close();
        if (p.waitFor() == 0) {
            tasker.setVariable("AAB_Privilege", "Root");
            privilege = "Root";
            hasPrivilegeFound = true;
        }
    } catch (Exception e) {
        /* Root check failed */
    }

    /* A8-A13: Check Write Secure Settings (The Surgical Fix) */
    if (!hasPrivilegeFound) {
        /* This replaces the 'App Info' action. We check our own context directly. */
        int permissionResult = context.checkSelfPermission("android.permission.WRITE_SECURE_SETTINGS");

        if (permissionResult == PackageManager.PERMISSION_GRANTED) {
            tasker.setVariable("AAB_Privilege", "Write Secure");
            privilege = "Write Secure";
            hasPrivilegeFound = true;
        }
    }

    /* A15-A18: Check Shizuku */
    if (!hasPrivilegeFound) {
        try {
            /* Try to access a standard service via Shizuku wrapper */
            if (tasker.getShizukuService("package") != null) {
                tasker.setVariable("AAB_Privilege", "Shizuku");
                privilege = "Shizuku";
                hasPrivilegeFound = true;
            }
        } catch (Exception e) {
            /* Shizuku not available */
        }
    }

    /* A20-A23: Check ADB Wifi (Refined Logic) */
    if (!hasPrivilegeFound) {
        int port = 5555; /* Default fallback */
        String portStr = null;

        /* Step 1: Try to read Tasker's preferred port */
        try {
            SharedPreferences sp = context.getSharedPreferences("net.dinglisch.android.tasker.preffy", 0);
            portStr = sp.getString("adbwp", null);
        } catch (Exception e) { 
            /* Preference read failed */
        }

        /* Step 2: If Pref failed, try System Property */
        if (portStr == null || portStr.length() == 0) {
            try {
                Process propProcess = Runtime.getRuntime().exec("getprop service.adb.tcp.port");
                BufferedReader reader = new BufferedReader(new InputStreamReader(propProcess.getInputStream()));
                String propLine = reader.readLine();
                reader.close();
                propProcess.destroy();

                if (propLine != null && propLine.trim().length() > 0) {
                    portStr = propLine.trim();
                }
            } catch (Exception e) { 
                /* Getprop failed */
            }
        }

        /* Step 3: Parse found string to int */
        if (portStr != null && portStr.length() > 0) {
            try {
                int parsedPort = Integer.parseInt(portStr);
                if (parsedPort > 0 && parsedPort <= 65535) {
                    port = parsedPort;
                }
            } catch (Exception e) { 
                /* Parsing failed, stick to 5555 */
            }
        }

        /* Step 4: Verify connectivity via Socket */
        try {
            Socket socket = new Socket();
            /* Connect to localhost with short timeout (200ms) */
            socket.connect(new InetSocketAddress("127.0.0.1", port), 200);
            socket.close();

            /* If we reached here, the connection succeeded */
            tasker.setVariable("AAB_Privilege", "ADB WiFi");
            privilege = "ADB WiFi";
            hasPrivilegeFound = true;
        } catch (Exception e) {
            /* Socket connection failed - ADB WiFi not active */
        }
    }

    /* A26-A33: Fallback to None */
    if (!hasPrivilegeFound) {
        tasker.setVariable("AAB_Privilege", "None");
        privilege = "None";

        /* A28: Notification Logic */
        if ("true".equals(notifyUse)) {
            NotificationManager nm = (NotificationManager) context.getSystemService(Context.NOTIFICATION_SERVICE);
            String channelId = "aab_setup_notification";
            int notificationId = 12345;

            /* Create Channel for Android O+ */
            if (Build.VERSION.SDK_INT >= 26) {
                NotificationChannel channel = new NotificationChannel(channelId, "AAB Setup", NotificationManager.IMPORTANCE_HIGH);
                channel.enableLights(true);
                channel.setLightColor(android.graphics.Color.CYAN); 
                nm.createNotificationChannel(channel);
            }

            /* Build Notification */
            Notification.Builder builder;
            if (Build.VERSION.SDK_INT >= 26) {
                builder = new Notification.Builder(context, channelId);
            } else {
                builder = new Notification.Builder(context);
            }

            builder.setContentTitle("Advanced Auto Brightness")
                   .setContentText("Please tap learn in order to learn how to grant Write Secure Settings permissions to this app.")
                   .setSmallIcon(android.R.drawable.ic_dialog_info)
                   .setOngoing(true)
                   .setNumber(0);

            /* A29/A30: Cancel/Notify */
            nm.notify(notificationId, builder.build());
        }

        /* A32: Flash Warning */
        tasker.showToast("⚠️ Unprivileged will draw a semi-transparent overlay and eat your battery. Not recommended! Please check notification for an alternative.");
    }
}

/* A35: Reporting Logic */
String finalPrivilege = tasker.getVariable("AAB_Privilege");
if (isAnon && finalPrivilege != null && !finalPrivilege.equals("None")) {
    tasker.showToast("Current privilege: " + finalPrivilege + ".");
}

import android.app.Activity;
import android.app.AlertDialog;
import android.content.DialogInterface;
import android.content.Context;
import android.content.ClipboardManager;
import android.content.ClipData;
import android.content.pm.PackageManager;
import android.view.View;
import android.widget.Button;
import android.widget.Toast;
import java.util.function.Consumer;
import io.reactivex.subjects.CompletableSubject;
import com.joaomgcd.taskerm.action.java.JavaCodeException;

/* 
 * 1. Retrieve the target package name from the Tasker variable %AAB_Package.
 *    If the variable is not set, throw an exception to inform the user.
 */
packageName = tasker.getVariable("AAB_Package");
if (packageName == null) {
    throw new JavaCodeException("Variable %AAB_Package is not set. Cannot check permission or generate ADB command.");
}

/*
 * 2. Check if the package already has the permission.
 *    The standard way to check a specific package's permission status (non-runtime)
 *    is using PackageManager. If it's already granted, we exit early.
 */
pm = context.getPackageManager();
if (pm.checkPermission("android.permission.WRITE_SECURE_SETTINGS", packageName) == android.content.pm.PackageManager.PERMISSION_GRANTED) {
    return;
}

/* 3. Construct the ADB command string. */
adbCommand = "adb shell pm grant " + packageName + " android.permission.WRITE_SECURE_SETTINGS";

/* 
 * 4. Create a Subject to block the script execution. 
 *    This ensures the script (and the temporary Activity) stays alive 
 *    until the user interacts with the dialog.
 */
waiter = CompletableSubject.create();

/* 5. Define the UI logic using doWithActivity to access a valid UI Context. */
uiConsumer = new Consumer() {
    accept(Object activityObj) {
        /* Cast the raw object to an Activity. */
        Activity activity = (Activity) activityObj;

        /* 
         * Create the Dialog Builder using the Activity context.
         * We use the fully qualified name to avoid BeanShell inner class resolution issues.
         */
        builder = new android.app.AlertDialog.Builder(activity);
        builder.setTitle("Permission Required");
        builder.setMessage("To enable more efficient Super Dimming functionality, the 'Write Secure Settings' permission must be granted.\n\nSince this is a system-protected permission, it cannot be granted within the app. You must use a computer with ADB (Android Debug Bridge).\n\nRun the following command:\n\n" + adbCommand);

        /* 
         * Define the 'Done' button. 
         * This closes the activity and unblocks the script.
         */
        builder.setPositiveButton("Done", new DialogInterface.OnClickListener() {
            onClick(DialogInterface dialog, int which) {
                activity.finish();
                waiter.onComplete();
            }
        });

        /* 
         * Define the 'Cancel' button.
         * Same behavior as Done, just closing everything.
         */
        builder.setNegativeButton("Cancel", new DialogInterface.OnClickListener() {
            onClick(DialogInterface dialog, int which) {
                activity.finish();
                waiter.onComplete();
            }
        });

        /* 
         * Define a 'Copy Command' button.
         * We set it here, but we will override its behavior in OnShowListener
         * so that it does NOT dismiss the dialog when clicked.
         */
        builder.setNeutralButton("Copy Command", new DialogInterface.OnClickListener() {
            onClick(DialogInterface dialog, int which) {
                /* Placeholder - will be overridden */
            }
        });

        /* Handle the back button or outside touch dismissal. */
        builder.setOnCancelListener(new DialogInterface.OnCancelListener() {
            onCancel(DialogInterface dialog) {
                activity.finish();
                waiter.onComplete();
            }
        });

        /* Create the dialog instance. */
        dialog = builder.create();

        /* 
         * Use OnShowListener to override the Neutral button's behavior.
         * This allows us to copy text without closing the dialog.
         */
        dialog.setOnShowListener(new DialogInterface.OnShowListener() {
            onShow(DialogInterface d) {
                Button copyBtn = ((AlertDialog)d).getButton(AlertDialog.BUTTON_NEUTRAL);
                copyBtn.setOnClickListener(new View.OnClickListener() {
                    onClick(View v) {
                        /* Copy to Clipboard. */
                        ClipboardManager clipboard = (ClipboardManager) activity.getSystemService(Context.CLIPBOARD_SERVICE);
                        ClipData clip = ClipData.newPlainText("ADB Command", adbCommand);
                        clipboard.setPrimaryClip(clip);

                        /* Show feedback. */
                        Toast.makeText(activity, "Command copied to clipboard!", Toast.LENGTH_SHORT).show();
                    }
                });
            }
        });

        /* Show the dialog. */
        dialog.show();
    }
};

/* 6. Execute the UI logic on the main thread. */
tasker.doWithActivity(uiConsumer);

/* 7. Wait here until the user dismisses the dialog (Done, Cancel, or Back). */
waiter.blockingAwait();

import java.util.Calendar;
import java.util.TimeZone;
import java.math.BigDecimal;
import android.location.Location;
import android.location.LocationManager;
import android.content.Context;

/* --- NOAA Solar Calculation Algorithm (Robust Inline Fix) --- */

/* 1. Get Inputs & Handle Defaults */
latStr = tasker.getVariable("AAB_Latitude");
lngStr = tasker.getVariable("AAB_Longitude");
dateStr = tasker.getVariable("AAB_Date");

/* APK FIX: Check standard Tasker variables if globals are empty or null string */
if (latStr == null || latStr.length() == 0) latStr = tasker.getVariable("gl_latitude");
if (lngStr == null || lngStr.length() == 0) lngStr = tasker.getVariable("gl_longitude");

/* FAILSAFE: Try standard %LOC variable (lat,lng) if still empty */
if (latStr == null || latStr.length() == 0) {
    locRaw = tasker.getVariable("LOC");
    if (locRaw != null && locRaw.indexOf(",") != -1) {
        parts = locRaw.split(",");
        if (parts.length >= 2) {
            latStr = parts[0];
            lngStr = parts[1];
        }
    }
}

lat = 0.0;
lng = 0.0;
dateSeconds = 0; /* Use 0 instead of 0L to avoid parser quirks */

/* Flags to track if we need to clear variables at the end */
clearDate = false;
clearLoc = false;

/* --- Handle Date (Flattened Logic) --- */
/* Attempt to parse dateStr if it exists */
dateParsed = false;
if (dateStr != null && dateStr.length() > 0) {
    try {
        dateSeconds = Long.parseLong(dateStr);
        dateParsed = true;
    } catch (Exception e) {
        /* Ignore error, will fallback below */
    }
}

/* Fallback to Today if parsing failed or string was empty */
if (!dateParsed) {
    dateSeconds = System.currentTimeMillis() / 1000;
    clearDate = true;
}

/* --- Handle Location --- */
locFound = false;

if (latStr != null && lngStr != null && latStr.length() > 0 && lngStr.length() > 0) {
    try {
        lat = Double.parseDouble(latStr);
        lng = Double.parseDouble(lngStr);
        locFound = true;
    } catch (NumberFormatException e) { 
        locFound = false;
    }
}

/* Check if we need to cleanup AAB vars later (if they were originally missing) */
if (tasker.getVariable("AAB_Latitude") == null) {
    clearLoc = true;
}

/* Try 'LastKnownLocation' if explicit location missing */
if (!locFound) {
    try {
        ctx = context.getApplicationContext();
        lm = (LocationManager) ctx.getSystemService(Context.LOCATION_SERVICE);
        bestLoc = null;

        providers = lm.getProviders(true);
        for (int i = 0; i < providers.size(); i++) {
            provider = (String) providers.get(i);
            l = lm.getLastKnownLocation(provider);
            if (l == null) continue;

            if (bestLoc == null || l.getTime() > bestLoc.getTime()) {
                bestLoc = l;
            }
        }

        if (bestLoc != null) {
            lat = bestLoc.getLatitude();
            lng = bestLoc.getLongitude();
            locFound = true;
        } 
    } catch (Exception e) {
        tasker.log("AAB: Location error: " + e.getMessage());
    }
}

/* APK FAILSAFE: Set not_found flag if location missing */
if (!locFound) {
    tasker.setVariable("not_found", "true");

    /* Perform cleanup before exiting to avoid leaving temporary vars */
    if (clearDate) {
        tasker.setVariable("AAB_Date", null);
    }
    if (clearLoc) {
        tasker.setVariable("AAB_Latitude", null);
        tasker.setVariable("AAB_Longitude", null);
    }
    return;
}

/* 2. Setup Calendar */
cal = Calendar.getInstance();
cal.setTimeInMillis(dateSeconds * 1000);
dayOfYear = cal.get(Calendar.DAY_OF_YEAR);
year = cal.get(Calendar.YEAR);
zoneOffset = cal.getTimeZone().getOffset(cal.getTimeInMillis()) / 3600000.0;

/* 
 * 3. Solar Math (Inline Loop)
 * Replaces nested methods/classes to prevent BeanShell syntax errors.
 * Indices: 0=Rise, 1=Set, 2=Dawn, 3=Dusk 
 */

/* Config arrays */
boolean[] isSunriseArr = { true, false, true, false };
double[] zenithArr = { 90.8333, 90.8333, 96.0, 96.0 };
double[] results = new double[4];

for (int i = 0; i < 4; i++) {
    isSunrise = isSunriseArr[i];
    zenith = zenithArr[i];

    lngHour = lng / 15.0;

    /* Calculate approximate time */
    if (isSunrise) {
        t_approx = dayOfYear + ((6.0 - lngHour) / 24.0);
    } else {
        t_approx = dayOfYear + ((18.0 - lngHour) / 24.0);
    }

    /* Sun's mean anomaly */
    M = (0.9856 * t_approx) - 3.289;

    /* Sun's true longitude */
    L = M + (1.916 * Math.sin(Math.toRadians(M))) + (0.020 * Math.sin(Math.toRadians(2 * M))) + 282.634;
    L = (L + 360.0) % 360.0;

    /* Right Ascension */
    RA = Math.toDegrees(Math.atan(0.91764 * Math.tan(Math.toRadians(L))));
    RA = (RA + 360.0) % 360.0;

    Lquadrant  = (Math.floor( L/90.0)) * 90.0;
    RAquadrant = (Math.floor(RA/90.0)) * 90.0;
    RA = RA + (Lquadrant - RAquadrant);
    RA = RA / 15.0;

    /* Sun's declination */
    sinDec = 0.39782 * Math.sin(Math.toRadians(L));
    cosDec = Math.cos(Math.asin(sinDec));

    /* Local hour angle */
    top = Math.cos(Math.toRadians(zenith)) - (sinDec * Math.sin(Math.toRadians(lat)));
    bottom = cosDec * Math.cos(Math.toRadians(lat));
    cosH = top / bottom;

    /* Handle Polar Cases in loop */
    if (cosH >  1) {
        results[i] = -1.0; /* Sun never rises */
    } else if (cosH < -1) {
        results[i] = -2.0; /* Sun never sets */
    } else {
        if (isSunrise) {
            H = 360.0 - Math.toDegrees(Math.acos(cosH));
        } else {
            H = Math.toDegrees(Math.acos(cosH));
        }
        H = H / 15.0;

        T = H + RA - (0.06571 * t_approx) - 6.622;
        UT = T - lngHour;
        localT = UT + zoneOffset;
        results[i] = (localT + 24.0) % 24.0;
    }
}

riseHour = results[0];
setHour  = results[1];
dawnHour = results[2];
duskHour = results[3];

/* Solar Noon Calculation */
noonHour = (riseHour + setHour) / 2.0;
if (setHour < riseHour) {
    noonHour = ((riseHour + setHour + 24.0) / 2.0) % 24.0;
}

/* 4. Process Results & Set Tasker Variables */
/* Helper to convert hour double to epoch string inline */
calBase = Calendar.getInstance();
calBase.setTimeInMillis(dateSeconds * 1000);
calBase.set(Calendar.HOUR_OF_DAY, 0);
calBase.set(Calendar.MINUTE, 0);
calBase.set(Calendar.SECOND, 0);
calBase.set(Calendar.MILLISECOND, 0);
startOfDay = calBase.getTimeInMillis() / 1000;

/* Inline epoch conversion helper logic */
String getEpoch(double h, long base) {
    long off = (long) (h * 3600.0);
    return String.valueOf(base + off);
}

if (riseHour < 0 || setHour < 0) {
    /* Polar Condition Logic */
    tasker.setVariable("AAB_SunStatus", "polar");

    if (riseHour == -2.0 || setHour == -2.0) {
        tasker.setVariable("ss_sunlight_duration", "1440"); /* Midnight Sun */
    } else {
        tasker.setVariable("ss_sunlight_duration", "0"); /* Polar Night */
    }

    /* Set safe defaults for times (Noon) */
    defaultTime = getEpoch(12.0, startOfDay);
    tasker.setVariable("ss_sunrise", defaultTime);
    tasker.setVariable("ss_sunset", defaultTime);
    tasker.setVariable("ss_civil_dawn", defaultTime);
    tasker.setVariable("ss_civil_dusk", defaultTime);
    tasker.setVariable("ss_solar_noon", defaultTime);

} else {
    /* Normal Solar Day */
    tasker.setVariable("AAB_SunStatus", "ok");

    riseEpoch = Long.parseLong(getEpoch(riseHour, startOfDay));
    setEpoch = Long.parseLong(getEpoch(setHour, startOfDay));

    tasker.setVariable("ss_sunrise", String.valueOf(riseEpoch));
    tasker.setVariable("ss_sunset", String.valueOf(setEpoch));
    tasker.setVariable("ss_civil_dawn", getEpoch(dawnHour, startOfDay));
    tasker.setVariable("ss_civil_dusk", getEpoch(duskHour, startOfDay));
    tasker.setVariable("ss_solar_noon", getEpoch(noonHour, startOfDay));

    durationMins = (setEpoch - riseEpoch) / 60.0;
    if (durationMins < 0) durationMins += 1440;
    tasker.setVariable("ss_sunlight_duration", String.valueOf(Math.round(durationMins)));
}

/* --- Cleanup --- */
if (clearDate) {
    tasker.setVariable("AAB_Date", null);
}
if (clearLoc) {
    tasker.setVariable("AAB_Latitude", null);
    tasker.setVariable("AAB_Longitude", null);
}

import java.math.BigDecimal;

/* --- Java Code: Dynamic Scale Calculation V13 Fix --- */

/* 1. Helper for parsing */
double safeParse(String val, double defVal) {
    if (val == null || val.length() == 0) return defVal;
    try {
        return Double.parseDouble(val);
    } catch (NumberFormatException e) {
        return defVal;
    }
}

/* 2. Retrieve Inputs */
/* We calculate 'now' directly to ensure it matches the System Time exactly */
long sysTime = System.currentTimeMillis() / 1000;
double now = (double) (sysTime % 86400);

/* Retrieve Schedule Variables */
/* Note: We rely on the Tasker actions A66-A81 having run successfully to populate these globals */
double morningStart = safeParse(tasker.getVariable("AAB_MorningStart"), 0.0);
double morningEnd = safeParse(tasker.getVariable("AAB_MorningEnd"), 0.0);
double morningDuration = safeParse(tasker.getVariable("AAB_MorningDuration"), 60.0);

double eveningStart = safeParse(tasker.getVariable("AAB_EveningStart"), 0.0);
double eveningEnd = safeParse(tasker.getVariable("AAB_EveningEnd"), 0.0);
double eveningDuration = safeParse(tasker.getVariable("AAB_EveningDuration"), 60.0);

double sunlightDuration = safeParse(tasker.getVariable("AAB_Sunlightduration"), 0.0);

/* Settings */
String polarStr = tasker.getVariable("AAB_PolarState");
boolean isPolar = (polarStr != null && polarStr.equals("true"));

double steepness = safeParse(tasker.getVariable("AAB_ScaleSteepness"), 4.0);
double dimSpread = safeParse(tasker.getVariable("AAB_DimSpread"), 0.0);
double scaleSpread = safeParse(tasker.getVariable("AAB_ScaleSpread"), 0.0);

/* 3. Safety Guards */
if (morningDuration < 1.0) morningDuration = 60.0;
if (eveningDuration < 1.0) eveningDuration = 60.0;

/* 4. Progress Calculation */
double progress = 0.0;
double time_v2 = now + 86400.0; 
double time_prev = now - 86400.0; /* Crucial for negative start times (previous day overlap) */

/* Helper to keep logic clean */
boolean inRange(double t, double s, double e) {
    return t >= s && t < e;
}

if (isPolar) {
    if (sunlightDuration > 1380) progress = 1.0;
    else progress = 0.0;
} else {
    /* Morning Ramp: 0.0 -> 1.0 */
    /* FIX: Check Now, Next Day (+24h), AND Prev Day (-24h) */
    if (inRange(now, morningStart, morningEnd)) {
        progress = (now - morningStart) / morningDuration;
    } 
    else if (inRange(time_v2, morningStart, morningEnd)) {
        progress = (time_v2 - morningStart) / morningDuration;
    }
    else if (inRange(time_prev, morningStart, morningEnd)) {
        progress = (time_prev - morningStart) / morningDuration;
    }

    /* Evening Ramp: 1.0 -> 0.0 */
    else if (inRange(now, eveningStart, eveningEnd)) {
        progress = 1.0 - ((now - eveningStart) / eveningDuration);
    }
    else if (inRange(time_v2, eveningStart, eveningEnd)) {
        progress = 1.0 - ((time_v2 - eveningStart) / eveningDuration);
    }
    else if (inRange(time_prev, eveningStart, eveningEnd)) {
        progress = 1.0 - ((time_prev - eveningStart) / eveningDuration);
    }

    /* Full Day (Between Morning End and Evening Start) */
    else {
        boolean isDay = false;

        // Check current day window
        if (now >= morningEnd && now <= eveningStart) isDay = true;
        // Check next day wrap (e.g. shifts > 86400)
        else if (time_v2 >= morningEnd && time_v2 <= eveningStart) isDay = true;
        // Check prev day wrap (e.g. shifts < 0)
        else if (time_prev >= morningEnd && time_prev <= eveningStart) isDay = true;

        if (isDay) progress = 1.0;
        else progress = 0.0; // Night
    }
}

/* Clamp Progress */
if (progress > 1.0) progress = 1.0;
if (progress < 0.0) progress = 0.0;

/* 5. Modifier Calculation (Sigmoid/Tanh) */
double x_factor = (progress - 0.5) * steepness;
double tanh_max = Math.tanh(steepness / 2.0);
double tanh_raw = Math.tanh(x_factor);

double modifier = 0.0;
if (Math.abs(tanh_max) > 0.000001) {
    modifier = tanh_raw / tanh_max;
}

/* 6. Calculate Final Dynamic Values */
double dim_dynamic_raw = 2.0 - (1.0 + (dimSpread / 100.0) * modifier);
BigDecimal dim_bd = new BigDecimal(dim_dynamic_raw).setScale(3, BigDecimal.ROUND_HALF_UP);

double scale_dynamic_raw = 1.0 + (scaleSpread / 100.0) * modifier;
BigDecimal scale_bd = new BigDecimal(scale_dynamic_raw).setScale(3, BigDecimal.ROUND_HALF_UP);

/* 7. Write Outputs */
tasker.setVariable("progress", String.valueOf(progress));
tasker.setVariable("modifier", String.valueOf(modifier)); 
tasker.setVariable("AAB_DimDynamic", dim_bd.toString());
tasker.setVariable("AAB_ScaleDynamic", scale_bd.toString());

/* Sanitize input variables by replacing comma decimal separators with periods. */
String[] varNames = new String[]{
    "suggestion_form1a",
    "suggestion_zone1end",
    "suggestion_form2b",
    "suggestion_form2c",
    "suggestion_zone2end"
};

for (int i = 0; i < varNames.length; i++) {
    varName = varNames[i];
    currentValue = tasker.getVariable(varName);

    /* Process only if the variable is set. */
    if (currentValue != null) {
        /* Replace all occurrences of comma with a period. */
        sanitizedValue = currentValue.replace(',', '.');

        /* Update the variable in Tasker with the sanitized value. */
        tasker.setVariable(varName, sanitizedValue);
    }
}

/* --- Java Code for Compression Graph Generation (Corrected) --- */

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;

/* --- Read all necessary Tasker variables --- */
spread_factor = %aab_scalespread / 100.0;
sim_day_scale = 1.0 + spread_factor;
sim_night_scale = 1.0 - spread_factor;
midpoint = %aab_scaletapermidpoint;
steepness = %aab_scaletapersteepness;
min_bright = %AAB_MinBright;
max_bright = %AAB_MaxBright;

/* --- Initialize lists to hold our data points --- */
day_scale_points_list = new ArrayList();
night_scale_points_list = new ArrayList();
brightness_labels_list = new ArrayList();

/* --- The main calculation loop --- */
for (double mapped_brightness = min_bright; mapped_brightness <= max_bright; mapped_brightness++) {

    /* Calculate the sigmoid taper effect */
    exponent = -steepness * (mapped_brightness - midpoint);
    compression_factor = 1.0 / (1.0 + Math.exp(exponent));
    taper_effect = 1.0 - compression_factor;

    /* --- THE FIX IS HERE --- */
    /* Replicate the original Tasker math: apply taper to the spread (scale - 1) */
    tapered_day_scale = 1.0 + (sim_day_scale - 1.0) * taper_effect;
    tapered_night_scale = 1.0 + (sim_night_scale - 1.0) * taper_effect;

    effective_day_scale = 0.0;
    effective_night_scale = 0.0;

    /* Apply dynamic caps and floors */
    if (mapped_brightness > 0) {
        dynamic_cap = max_bright / mapped_brightness;
        dynamic_floor = 2.0 - dynamic_cap;

        effective_day_scale = Math.min(tapered_day_scale, dynamic_cap);
        effective_night_scale = Math.max(tapered_night_scale, dynamic_floor);
    } else {
        effective_day_scale = sim_day_scale;
        effective_night_scale = sim_night_scale;
    }

    day_scale_points_list.add(new Double(effective_day_scale));
    night_scale_points_list.add(new Double(effective_night_scale));
    brightness_labels_list.add(new Double(mapped_brightness));
}

/* --- Join the lists into comma-separated strings --- */
day_scale_points_str = new StringBuffer();
night_scale_points_str = new StringBuffer();
brightness_labels_str = new StringBuffer();

for (i = 0; i < brightness_labels_list.size(); i++) {
    if (i > 0) {
        day_scale_points_str.append(",");
        night_scale_points_str.append(",");
        brightness_labels_str.append(",");
    }

    day_scale = new BigDecimal(((Double) day_scale_points_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP);
    day_scale_points_str.append(day_scale.toString());

    night_scale = new BigDecimal(((Double) night_scale_points_list.get(i)).doubleValue()).setScale(3, BigDecimal.ROUND_HALF_UP);
    night_scale_points_str.append(night_scale.toString());

    brightness_label = new BigDecimal(((Double) brightness_labels_list.get(i)).doubleValue()).setScale(0, BigDecimal.ROUND_HALF_UP);
    brightness_labels_str.append(brightness_label.toString());
}

/* --- Set the final variables for Tasker to use --- */
tasker.setVariable("day_scale_points", day_scale_points_str.toString());
tasker.setVariable("night_scale_points", night_scale_points_str.toString());
tasker.setVariable("brightness_labels", brightness_labels_str.toString());

/* --- Java Code to Show a Native Overlay Dialog --- */

import android.app.AlertDialog;
import android.content.DialogInterface;
import android.os.Handler;
import android.os.Looper;
import android.view.WindowManager;

final String combinedData = "%par1";

String[] parts = combinedData.split(",");

final String lux = (parts.length > 0) ? parts[0] : "Error";
final String brightness = (parts.length > 1) ? parts[1] : "Error";

new Handler(Looper.getMainLooper()).post(new Runnable() {
    public void run() {
        AlertDialog.Builder builder = new AlertDialog.Builder(context);
        builder.setTitle("Delete override point?");

        builder.setMessage("Are you sure you want to remove this data point?\n\nLux: " + lux + ", Brightness: " + brightness);

        /* "Yes" button sets a global variable to "yes" */
        builder.setPositiveButton("Yes", new DialogInterface.OnClickListener() {
            public void onClick(DialogInterface dialog, int which) {
                tasker.setVariable("AAB_JavaDialogResponse", "yes");
                dialog.dismiss();
            }
        });

        /* "No" button sets the same variable to "no" */
        builder.setNegativeButton("No", new DialogInterface.OnClickListener() {
            public void onClick(DialogInterface dialog, int which) {
                tasker.setVariable("AAB_JavaDialogResponse", "no");
                dialog.dismiss();
            }
        });

        /* Ensure the variable is set if the dialog is cancelled */
        builder.setOnCancelListener(new DialogInterface.OnCancelListener() {
            public void onCancel(DialogInterface dialog) {
                tasker.setVariable("AAB_JavaDialogResponse", "cancelled");
            }
        });

        AlertDialog dialog = builder.create();
        dialog.getWindow().setType(WindowManager.LayoutParams.TYPE_APPLICATION_OVERLAY);
        dialog.show();
    }
});

javascript:(function(){
   var targetLux = %par11;
   var targetBright = %par12;
   // Dataset 2 is "Your Overrides"
   var dataset = brightnessChart.data.datasets[2].data;

   // Find the point
   var idx = dataset.findIndex(function(p){ return p.x == targetLux && p.y == targetBright });

   if(idx !== -1){
      // Remove the point
      dataset.splice(idx, 1);

      // Update chart WITHOUT animation ('none')
      brightnessChart.update('none'); 
   }
})();
